/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case ('JP-SMBC-XML'):
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('Btchbkg', gSKS_EFT_Bank.W01, true);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('InitiatingPartyId', gSKS_EFT_Bank.W02);
            }
            if(gSKS_EFT_Bank.W03)
            {
                this.updateBankFields('DbtrAgtBrnchId', gSKS_EFT_Bank.W03);
            }
            break;
        case ('JP-SMBC-XML-US'):
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('Btchbkg', gSKS_EFT_Bank.W01, true);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('InitiatingPartyId', gSKS_EFT_Bank.W02);
            }
            if(gSKS_EFT_Bank.W03)
            {
                this.updateBankFields('DebtorId', gSKS_EFT_Bank.W03);
            }
            break;
    }
}
