/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    if(gSKS_EFT_Bank.W01)
    {
        this.updateBankFields('TransHandlngCode', gSKS_EFT_Bank.W01);
    }
}

/// <summary>
/// Updating vednor code lookpus from BE0X and Legacu code loookups
/// </summary>
protected void updateVendorCodelookupValues()
{
    if(gSKS_EFT_VendorPaym.WBeneMailHandling)
    {
        this.updateVendorFields('MailHandlngCode', gSKS_EFT_VendorPaym.WBeneMailHandling, true);
    }
}
