/// <summary>
/// Updating vendor code lookpus from BE0X and Legacy code loookups
/// </summary>
protected void updateVendorCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case 'US-CHASE-GFF-1019':
            if(gSKS_EFT_VendorPaym.BE01)
            {
                this.updateVendorFields('BackOfficeId', gSKS_EFT_VendorPaym.BE01);
            }
            if(gSKS_EFT_VendorPaym.BE02)
            {
                this.updateVendorFields('RmtFormId', gSKS_EFT_VendorPaym.BE02);
            }
            if(gSKS_EFT_VendorPaym.WBeneAdvice)
            {
                this.updateVendorFields('RmtSummaryInd', gSKS_EFT_VendorPaym.WBeneAdvice, true);
            }
            if(gSKS_EFT_VendorPaym.WBeneMailHandling)
            {
                this.updateVendorFields('RmtDeliveryMode', gSKS_EFT_VendorPaym.WBeneMailHandling, true);
            }

            break;
    }
}
