protected container getFileFormatFieldsMetaData(str _formatID)
{
    container   paymSpecFieldsList;

    switch(_formatID)
    {
        case ('US-CHASE-XML-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_US_CHASE_XML_AR();
            break;
        case ('US-CHASE-XML-CA-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_US_CHASE_XML_CA_AR();
            break;
        case ('US-CHASE-XML-US-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_US_CHASE_XML_US_AR();
            break;
    }

    return paymSpecFieldsList;
}

