/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case ('US-COMERICA-XML'):
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('CustomerId', gSKS_EFT_Bank.W01);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('CorporationNumber', gSKS_EFT_Bank.W02);
            }
            break;
    }
}

/// <summary>
/// Updating the direct debit bank code lookpups from the W0X fields.
/// </summary>
protected void updateARBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case ('US-COMERICA-XML'):
            if(gSKS_EFTAR_Bank.W01)
            {
                this.updateARBankFields('CustomerId', gSKS_EFTAR_Bank.W01);
            }
            if(gSKS_EFTAR_Bank.W02)
            {
                this.updateARBankFields('CorporationNumber', gSKS_EFTAR_Bank.W02);
            }
            break;
    }
}
