/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case 'US-PNC-PAFF-v20140301':
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('BlkMailCd', gSKS_EFT_Bank.W01);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('RepWireCd', gSKS_EFT_Bank.W02);
            }
            if(gSKS_EFT_Bank.W03)
            {
                this.updateBankFields('BussinessApplFormat', gSKS_EFT_Bank.W03);
            }
            if(gSKS_EFT_Bank.W04)
            {
                this.updateBankFields('CanIndicator', gSKS_EFT_Bank.W04);
            }
            break;
    }
}

/// <summary>
/// Updating vendor code lookpus from BE0X and Legacy code loookups
/// </summary>
protected void updateVendorCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case 'US-PNC-PAFF-v20140301':
            if(gSKS_EFT_VendorPaym.WBeneAdvice)
            {
                this.updateVendorFields('ChkInstr', gSKS_EFT_VendorPaym.WBeneAdvice, true);
            }
            break;
    }
}
