        codeLookupList = ConIns(codeLookupList,conLen(codeLookupList)+1,['US-SUNTRUST-XML','ChqTp','Cheque Type']);
        codeLookupList = ConIns(codeLookupList,conLen(codeLookupList)+1,['US-SUNTRUST-XML','DlvryMtd','Delivery Method']);
        codeLookupList = ConIns(codeLookupList,conLen(codeLookupList)+1,['US-SUNTRUST-XML','InstrPrty','Instruction Priority']);
        codeLookupList = ConIns(codeLookupList,conLen(codeLookupList)+1,['US-SUNTRUST-XML','ChrgBr','Charge Bearer']);

        return codeLookupList;

    }

