protected container getControlFormatMetaData(str _formatID)
{
    container   ControlFormatList;

    //Metadata column names of data elements
    //FileFormatID,ControlFileFormatID,ControlFileDescription

    switch (_formatID)
    {
        case ('US-SVB-XML'):
            ControlFormatList = this.getControlFormatMetaData_US_SVB_XML();
            break;

    }

    return ControlFormatList;

}

private container getControlFormatMetaData_US_SVB_XML()
{
    container   ControlFormatList;

    ControlFormatList = ConIns(ControlFormatList,conLen(ControlFormatList)+1,['US-SVB-XML','US-SVB-XML','US-SVB-XML file authentication record']);

    return ControlFormatList;

}

