switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode) { case #OTC894: case #CBFT896: case #WIRE574: //SKG#VSTS:10574 value = 'WIRE'; break; case #DFT391: case #CBFT392: case #FT499: case #CB_WIRE: case #CB392: case #CBFT574: //SKG#VSTS:7453 case #DFT603: case #CBFT604: case #USFCP821: case #RT453: case #CBFT442: case #DFT441: case #WIRE_WL: case #WIRE392: //SKG#VSTS:10575 case #XPRS888: //SKG#VSTS:12473 case #RTGS605: //SKG#VSTS:14212 case #PPW_WL: //SKS#VSTS:14271 case #PPW_PH_WL: //SKS#DevOps:14542 case #RTGS539: //SKS#DevOps:15853 case #TAX927: //SKS#DevOps:15853 case #TAX947: //SKS#DevOps:15853 case #IFT_CB392: //SKS#DevOps:15853 case #CDT862: //SKS#DevOps:15853 value = 'URGP'; break; case #CACH460: case #ACHCCD: case #ACHPPD: case #ACHCTX: case #ACH502: case #ACH503: case #ACH_KID: case #ACH_PG: case #ACH_BG: case #DC417: case #CHECK416: case #IBRS430: case #ACH964: case #CHECK815: case #DFT574: //SKG#VSTS:7453 case #ACHCCP: case #ACHPPP: case #CCDP14: case #CTX17: case #CHK19: case #CTX942: case #CPS706: case #ACH780: case #DOM317: case #DOM555: case #ACH208: case #DOM303: case #DOM903: case #DOM325: case #DOM332: case #DOM789: case #DOM548: case #ACH150: case #DOM441: case #DE397: case #PLG400: case #ATPAY403: case #CHK405: case #LLG408: case #TLPAY451: case #PRL398: case #PRL401: case #BKT410: case #PRL414: case #PLG527: case #GIRO421: case #ATPAY424: case #CHK425: case #PRL426: case #CHK496: case #CHK834: case #BWPMT835: case #GIRO527: case #CHECK405: case #BPAY889: case #ACH_WL: case #ACH410: //SKG#VSTS:10575 case #WIRE391: //SKG#VSTS:10575 case #PRL422: //SKG#VSTS:11471 case #SSK853: //SKG#VSTS:11518 case #TAX592: //SKG#VSTS:12473 case #VAT984: //SKG#VSTS:12473 case #CTX950: //SKG#VSTS:13477 case #TAX908: //SKG#VSTS:15853 value = 'NURG'; break; case #BKT500: case #BKT393: case #BKT602: case #BKT440: case #BKT547: //SKG#VSTS:10808 value = 'BKTR'; break; case #ACH_DC: value = (mSKS_EFT_FileFormatPaymSpec.FormatId == 'US-CITICONNECT-XML-IT' ? 'SEPA' : 'NURG');