protected void node_CdtTrfTxInf_CdtrAcct(XmlElement _nodeCdtTrfTxInf)
{
    //------------------------------------------------------------------------------------------------------
    //          2.80: CdtrAcct
    //              Id
    //                  Othr
    //                  Id
    //              Tp/Ptry
    //------------------------------------------------------------------------------------------------------

    XmlElement nodeCdtrAcct;
    XmlElement nodeCdtrAcctId;
    XmlElement nodeCdtrAcctIdOthr;
    XmlElement nodeCdtrAcctTp;
    VendBankAccount mVendBankAccount;
    str acctType, iban, acctNum;
    int length;
    boolean leadZeros = false;
    boolean acctTypeCd = false;
    boolean acctTypePrtry = false;
    boolean vendAcctName = false;
    boolean printCdtrAcct = true;

    iban        = mSKS_EFT_FileDetail.DepBankIBAN;
    acctNum     = mSKS_EFT_FileDetail.DepBankAcct;

    acctType    = this.CharacterReplacement(SKS_EFT_Format::getCodelookupvalue('BenAcctTpCd', mSKS_EFT_FileDetail.RecId)); //SKS_FDD_10590

    if(acctType == '')
    {
        if(mSKS_EFT_FileDetail.DepBankAcctType == BankAccountType::SavingsAccount)
        {
            acctType = 'SVGS';
        }
        else
        {
            acctType = 'CACC';
        }
    }
    switch(mSKS_EFT_FileFormatPaymSpec.FormatId)
    {
        case 'US-CITICONNECT-XML-AE':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #CBFT574:
                    length  = 34;
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-AR':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #BKT440:
                    iban        = '';
                    acctTypeCd  = true;
                    length      = 35;
                    break;
                case #DFT441:
                    acctTypeCd  = true;
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-AT':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #ACH_SEPA:
                    acctNum = '';
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-AU':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #BKT393:
                    iban    = '';
                    length  = 10;
                    break;
                case #CB392:
                case #DOM391:
                    iban    = '';
                    length  = 34;
                    break;
                case #DE397:
                    iban    = '';
                    length  = 9;
                    break;
                case #PRL398:
                    iban    = '';
                    vendAcctName = true;
                    break;
                case #BPAY889:
                    printCdtrAcct = false;
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-BE':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #ACH_SEPA:
                    acctNum = '';
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-BR':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #BKT440:
                case #RT453:
                    iban        = '';
                    acctTypeCd  = true;
                    length      = 35;
                    break;
                case #DL454:
                    iban        = '';
                    acctTypeCd  = true;
                    length      = 10;
                    break;
                case #TLPAY451:
                    iban        = '';
                    acctTypeCd  = true;
                    length      = 20;
                    break;
                case #DOM441:
                    iban        = '';
                    acctTypeCd  = true;
                    length      = 34;
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-CA':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #BKT602:
                case #CACH460:
                case #CBFT604:
                case #DFT603:
                    iban        = '';
                    acctTypeCd  = true;
                    break;
                case #CPS706:
                    iban        = '';
                    length = 35;
                    acctTypeCd  = true;
                    break;
                case #ACH780:
                    iban        = '';
                    length = 12;
                    break;
            }
            break;
        case 'US-CITICONNECT-XML-CL':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #BKT440:
                    iban        = '';
                    acctTypeCd  = true;
