                                SKS_EFT_Format::xmlAppendChildNodeText(xmlDoc, nodeInstrForCdtrAgt, 'InstrInf', subStr('Acc No ' + msKS_EFT_FileDetail_CreditParty.CrePartyAcct, 1, 30));

