                            SKS_EFT_Format::xmlAppendChildNodeText(xmlDoc, nodeInstrForCdtrAgt, 'InstrInf', subStr('FFC ' + this.CharacterReplacement(msKS_EFT_FileDetail_CreditParty.CrePartyName), 1, 30));

