                break;
            case 'US-CITICONNECT-WL-XML-US':
                switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
                {
                    case #ACH_WL:

                        //      PstlAdr
                        nodePstlAdr = _nodeCdtr.appendChild(xmlDoc.createElement('PstlAdr'));

