                            {
                                address = address + ' ' + mRemittanceCity;
                            }
                            if(mRemittanceCountryRegionId)
                            {
                                address = address + ' ' + mRemittanceCountryRegionId;
                            }
                            if(mRemittanceState)
                            {
                                address = address + ' ' + mRemittanceState;
