            break;
        case 'US-CITICONNECT-XML-MY':
            brnchId = true;
            country = 'MY';
            break;
        case 'US-CITICONNECT-XML-PL':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
