                case #CHK19:
                case 'CAMAL':
                case 'CHK19USFED':
                case 'CHK19CAFED':
                case 'CHKUSFED02':
                case 'CHKUSFED03':
                case 'CHKCAFED02':
                case 'CHKCAFED03':
                    rtg     = mSKS_EFT_FileDetail.BankBankTransit;
