                    rtg = '';
                    len = 11;
                    benBankName = true;
                    break;
            }
            break;

        case 'US-CITICONNECT-WL-XML-AZ':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #WIRE_WL:
                    rtg = '';
                    len = 11;
                    benBankName = true;
