            }
            break;

        case 'US-CITICONNECT-WL-XML-DZ':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #WIRE_WL:
                    rtg = '';
                    len = 11;
                    benBankName = true;
                    break;
            }
            break;

        case 'US-CITICONNECT-WL-XML-GH':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #WIRE_WL:
                    swift = '';
                    benBankName = true;
                    break;
            }
            break;

        case 'US-CITICONNECT-WL-XML-GT':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #WIRE_WL:
                    rtg = '';
