                            this.getLogisticsAddressCountryISO(mSKS_EFT_FileDetail_BankAccount.WOrdPartyCountryRegionId) == 'GB')
                    {
                        if(mSKS_EFT_FileDetail.DepBankTransit && mSKS_EFT_FileDetail.DepBankAcct)
