                    }

                    benBankName = true;
                    break;
            }
            break;
        case 'US-CITICONNECT-WL-XML-FJ':
            switch(mSKS_EFT_FileFormatPaymSpec.TransactionCode)
            {
                case #WIRE_WL:
                    if(swift)
                    {
