            {
                locRecord = conPoke(locRecord, i, strRem(conPeek(locRecord,i), '\r\n'));
            }

            locString1 = strLRTrim(conPeek(locRecord, 2));
            locString2 = strLRTrim(conPeek(locRecord, 3));
            locString3 = strLRTrim(conPeek(locRecord, 4));
            locString4 = strLRTrim(conPeek(locRecord, 5));

            if(!System.String::IsNullOrEmpty(locString1))
            {
                if(locString1 == '01' ||
                   locString1 == '03' ||
                   locString1 == '02')
                {
                    FirstRecordFound = true;
                }
            }
            if(!System.String::IsNullOrEmpty(locString2))
            {
                SecondRecordFound = true;
            }
            else if(!System.String::IsNullOrEmpty(locString3))
            {
                SecondRecordFound = true;
            }
            if(!System.String::IsNullOrEmpty(locString4))
            {
                ThirdRecordFound = true;
            }
            if(FirstRecordFound     &&
               SecondRecordFound    &&
               ThirdRecordFound)
            {
                break;
            }
        }
    }

    if(FirstRecordFound     &&
       SecondRecordFound    &&
       ThirdRecordFound)
    {
        //ALl Qualifers show this a
        classId = classIdGet(this);
        rtnval = classId2Name(ClassId);
    }
    if(locCommaIo)
    {
        locCommaIo.finalize();
        locCommaIo = null;
    }

    return rtnval;
}

public SKS_EFT_RF_File getSKS_EFT_RF_File()
{

    if(mSKS_EFT_RF_SentFile != null)
    {
        mSKS_EFT_RF_File.addSentFile(mSKS_EFT_RF_SentFile);
        mSKS_EFT_RF_SentFile = null;
    }

    return mSKS_EFT_RF_File;
}

private void processNode(container locRecord)
{
    //SKS_EFT_RF_SentFile locSKS_EFT_RF_SentFile;
    System.String transCode, fileKey, transKey, fileTransStatus, fileTransReason;
    ;

    transCode = strLRTrim(conPeek(locRecord, 2));
    fileKey = strLRTrim(conPeek(locRecord, 3));
    transKey = strLRTrim(conPeek(locRecord, 4));
    fileTransStatus = strLRTrim(conPeek(locRecord, 5));
    fileTransReason = strLRTrim(conPeek(locRecord, 6));

    //locSKS_EFT_RF_SentFile = new SKS_EFT_RF_SentFile();

    mSKS_EFT_RF_Transaction = new SKS_EFT_RF_Transaction();
    if(mSKS_EFT_RF_Transaction != null)
    {
        mSKS_EFT_RF_SentFile.addTransaction(mSKS_EFT_RF_Transaction);
    }

    //Set the File Reference Number
    if(transCode == '01')
    {
        if(!System.String::IsNullOrEmpty(fileKey))
        {
            mSKS_EFT_RF_SentFile.setID(fileKey);
        }
        if(!System.String::IsNullOrEmpty(fileTransStatus))
        {
            mSKS_EFT_RF_SentFile.setStatus(fileTransStatus);
        }
        if(!System.String::IsNullOrEmpty(fileTransReason))
        {
            mSKS_EFT_RF_SentFile.setStatusReason(fileTransReason);
        }
    }
    else if(transCode == '03' || transCode == '02')
    {
        if(!System.String::IsNullOrEmpty(transKey))
        {
            mSKS_EFT_RF_Transaction.setID(transKey);
        }
        if(!System.String::IsNullOrEmpty(fileTransStatus))
        {
            mSKS_EFT_RF_Transaction.setStatus(fileTransStatus);
        }
        if(!System.String::IsNullOrEmpty(fileTransReason))
        {
            mSKS_EFT_RF_Transaction.setStatusReason(fileTransReason);
        }
    }
}

