{
    SKS_EFT_Setup setup;

    select setup;

    _fds.object(fieldNum(LedgerJournalTrans, PaymMode)).setValue(setup.CNPDefaultPaymMode);
    _fds.object(fieldNum(LedgerJournalTrans, PaymSpec)).setValue(setup.CNPDefaultPaymSpec);
}

