Method.Source:
//JPMCHASE-PWS
//Standard Positive Pay
//  1        1     Transaction Indicator
//  2-18     17    Account Number
//  19-28    10    Check Number
//  29-34    6     Check Date (MMDDYY)
//  35-45    11    Check Amount
//  46-75    30    Additional Data
//  76-115   40    Payee Line 1
//  116-155  40    Payee Line 2
protected str txtDetail()
{
    int RecordLength;
    int Mult;
    str ItemType;
    System.Text.StringBuilder sb = new System.Text.StringBuilder();
    ;

    //Convert negative void numbers to positives
    Mult = 1;
    if(mSKS_PP_FileDetail.ChkType != SKS_PP_ChequeType::Cheque)
    {
        ItemType = 'C';
        if(mSKS_PP_FileDetail.chkcuryamt < 0)
        {
            Mult = -1;
        }
    }
    else
    {
        ItemType = 'I';
    }


    RecordLength =  mSKS_PP_FileFormat.RecordLen;

    sb.Append(ItemType);
    sb.Append(SKS_PP_StringFormats::strFill(17, mSKS_PP_FileDetail.BnkBankAcct, SKS_PP_FillCharacter::Zero, SKS_PP_FillSide::Left));
    sb.Append(SKS_PP_StringFormats::strFill(10, mSKS_PP_FileDetail.ChkRefNbr, SKS_PP_FillCharacter::Zero, SKS_PP_FillSide::Left));
    sb.Append(SKS_PP_StringFormats::strDateMMDDYY(mSKS_PP_FileDetail.ChkDocDate));
    sb.Append(strRTrim(StrLTrim(SKS_PP_StringFormats::strFill(11, Num2Str(mSKS_PP_FileDetail.ChkCuryAmt * Mult,1, 2, 1,0), SKS_PP_FillCharacter::Zero, SKS_PP_FillSide::Left))));
    sb.Append(SKS_PP_StringFormats::strFill(30, mSKS_PP_FileDetail.VendorAccount, SKS_PP_FillCharacter::Space, SKS_PP_FillSide::Right));
    sb.Append(SKS_PP_StringFormats::strFill(40, mSKS_PP_FileDetail.VendName, SKS_PP_FillCharacter::Space, SKS_PP_FillSide::Right));
    sb.Append(SKS_PP_StringFormats::strFill(40, '', SKS_PP_FillCharacter::Space, SKS_PP_FillSide::Right));

    return sb.ToString();
}
