[Form]
public class SKS_BR_FileDetailTranMapping extends FormRun
{
    SKS_BR_FileFormat mainFileFormat;
    boolean           isCalledFromCCReconciliation;// SKS_FDD_13903

}

void callingFormChange()
{
    boolean enable;
    SKS_BR_FileFormatDetail SKS_BR_FileFormatDetail;
    SKS_PP_Setup sks_PP_Setup;

    // Enable controls if the newly focused calling line is BAI2 or
    // it's CSV or Fixed Length and has Transaction Type mapped.
    // Otherwise disable them.
    ;
    // If there's no format ID then disable
    if (strlen(mainFileFormat.FormatID) != 0)
    {
        enable = true;
    }
    else
    {
        enable = false;
    }

    if (enable && !isCalledFromCCReconciliation && //SKS_FDD_13903
            ((mainFileFormat.FileType == SKS_BR_FileType::CSV ||
            mainFileFormat.FileType == SKS_BR_FileType::FixedField) &&
            mainFileFormat.APIFormatType != SKS_EB_APIFormatType::API)) 
    {
        // See if there is a 'Transaction type' mapping for this format id
        select firstonly recId
                from SKS_BR_FileFormatDetail
                where SKS_BR_FileFormatDetail.FormatID == mainFileFormat.FormatID &&
                    SKS_BR_FileFormatDetail.FieldType == SKS_BR_FieldType::TxnType;

        if (SKS_BR_FileFormatDetail.RecId == 0)
        {
            enable = false;
        }
    }
    else if(enable && isCalledFromCCReconciliation &&
        (mainFileFormat.CCR_FileType == SKS_CCR_FileType::Delimited ||
            mainFileFormat.CCR_FileType == SKS_CCR_FileType::FixedField))
    {
        // See if there is a 'Transaction type' mapping for this format id
        select firstonly recId
            from SKS_BR_FileFormatDetail
            where SKS_BR_FileFormatDetail.FormatID == mainFileFormat.FormatID &&
                SKS_BR_FileFormatDetail.CCR_FieldType == SKS_CCR_FieldType::TxnType;
        if (SKS_BR_FileFormatDetail.RecId == 0)
        {
            enable = false;
        }
    }

    select firstonly sks_PP_Setup;
    if (enable == true)
    {
        if (sks_PP_Setup.BREdition == SKS_BR_Edition::Standard)
        {
            SKS_BR_FileFormatTranMapping_BankAccountSpecifier.visible(false);
            SKS_BR_FileFormatTranMapping_MatchingRuleGroupId.visible(false);
            SKS_BR_FileFormatTranMapping_GLMappingRuleGroupId.visible(false);
            SKS_BR_FileFormatTranMapping_DoNotImport.visible(false);
            SKS_BR_FileFormatTranMapping_BankTranType.visible(false);
            TabFinancialDimensions.visible(false);
        }
        else
        {
            SKS_BR_FileFormatTranMapping_BankAccountSpecifier.visible(true);
            SKS_BR_FileFormatTranMapping_MatchingRuleGroupId.visible(true);
            SKS_BR_FileFormatTranMapping_GLMappingRuleGroupId.visible(true);
            SKS_BR_FileFormatTranMapping_DoNotImport.enabled(true);
            SKS_BR_FileFormatTranMapping_BankTranType.enabled(true);
            TabFinancialDimensions.visible(true);
        }
    }

    if (mainFileFormat.DebitCreditIdentifier == noyes::No)
    {
        SKS_BR_FileFormatTranMapping_BankDebitCredit.visible(false);
        if (sks_PP_Setup.BREdition == SKS_BR_Edition::Standard)
        {
            SKS_BR_FileFormatTranMapping_FileTranType.visible(false);
        }
        else
        {
            SKS_BR_FileFormatTranMapping_FileTranType.visible(true);
        }
    }
    else
    {
        SKS_BR_FileFormatTranMapping_BankDebitCredit.visible(true);
        SKS_BR_FileFormatTranMapping_FileTranType.visible(true);
    }

    SKS_BR_FileFormatTranMapping_ds.object(fieldNum(SKS_BR_FileFormatTranMapping,CAMT053_TransGranularity)).visible(
            mainFileFormat.FormatID != '' 
            && !isCalledFromCCReconciliation
        && mainFileFormat.FileType == SKS_BR_FileType::CAMT_053_001_02
        && mainFileFormat.CAMT053OverrideDetailLevel == NoYes::Yes);
    SKS_BR_FileFormatTranMapping_ds.object(fieldNum(SKS_BR_FileFormatTranMapping,CAMT053_TransGranularityXMLPaths)).visible(
            mainFileFormat.FormatID != ''
            && !isCalledFromCCReconciliation
        && mainFileFormat.FileType == SKS_BR_FileType::CAMT_053_001_02
        && mainFileFormat.CAMT053OverrideDetailLevel == NoYes::Yes);
    // Go ahead and set the grid control properties
    Grid.enabled(enable);




}

public boolean confirmBankSpecifier()
{
    SKS_BR_FileFormatTranMapping origSKS_BR_FileFormatTranMapping;
    SKS_BR_TranTypeBankAccount SKS_BR_TranTypeBankAccount;
    DialogButton diagBut;
    boolean ret = true;

    origSKS_BR_FileFormatTranMapping = SKS_BR_FileFormatTranMapping.orig();

    // If changed from Specific to All, remove the Specific bank account records
    if(SKS_BR_FileFormatTranMapping.BankAccountSpecifier == SKS_BR_BankAccountSpecifier::All && origSKS_BR_FileFormatTranMapping.BankAccountSpecifier == SKS_BR_BankAccountSpecifier::Specific)
    {
        diagBut = Box::yesNo(
            "@SKS2339",
            DialogButton::Yes,
            "@SKS313");

        if (diagBut == DialogButton::Yes)
        {
            delete_from SKS_BR_TranTypeBankAccount where
                SKS_BR_TranTypeBankAccount.FileTranTypeRecId == SKS_BR_FileFormatTranMapping.RecId;

            ret = true;
        }
        else
        {
            ret = false;
        }
    }
    return ret;
}

public void enableDisableBankAccountButton()
{
    if(SKS_BR_FileFormatTranMapping.BankAccountSpecifier == SKS_BR_BankAccountSpecifier::All)
    {
        btnSKS_BR_TranTypeBankAccount.enabled(false);
    }
    else
    {
        btnSKS_BR_TranTypeBankAccount.enabled(true);
    }
}

void enableDisableDimension()
{
    if(SKS_BR_FileFormatTranMapping.GLMappingRuleGroupId)
    {
        TabFinancialDimensions.enabled(true);
    }
    else
    {
        TabFinancialDimensions.enabled(false);
    }
}

public void init()
{
    super();

    if (element.args().dataset() == tablenum(SKS_BR_FileFormat))
    {
        mainFileFormat = element.args().record();

        //SKS_FDD_13903 Begin
        if(mainFileFormat.CCR_IsCCRFormat == NoYes::Yes)
        {
            this.design().caption('Credit card reconciliation - Transaction type setup');
            isCalledFromCCReconciliation = true;
        }
        //SKS_FDD_13903 End
    }
    else
    {
        throw error("@SYS96590");
    }


    element.callingFormChange();
}

void refreshForm()
{
    SKS_BR_FileFormatTranMapping_ds.research();
}

public boolean validateBankAccountSpecifier()
{
    SKS_BR_FileFormatTranMapping dupSKS_BR_FileFormatTranMapping;
    boolean ret = true;

    if(SKS_BR_FileFormatTranMapping.BankAccountSpecifier == SKS_BR_BankAccountSpecifier::All)
    {
        select firstOnly FormatID, FileTranType, RecId from dupSKS_BR_FileFormatTranMapping where
            dupSKS_BR_FileFormatTranMapping.FormatID == SKS_BR_FileFormatTranMapping.FormatID &&
            dupSKS_BR_FileFormatTranMapping.FileTranType ==  SKS_BR_FileFormatTranMapping.FileTranType &&
            dupSKS_BR_FileFormatTranMapping.RecId != SKS_BR_FileFormatTranMapping.RecId;

        if(dupSKS_BR_FileFormatTranMapping)
        {
            error("@SKS2337");
            ret = false;
        }
    }
    else //Specific
    {
        select firstOnly FormatID, FileTranType, RecId from dupSKS_BR_FileFormatTranMapping where
            dupSKS_BR_FileFormatTranMapping.FormatID == SKS_BR_FileFormatTranMapping.FormatID &&
            dupSKS_BR_FileFormatTranMapping.FileTranType ==  SKS_BR_FileFormatTranMapping.FileTranType &&
            dupSKS_BR_FileFormatTranMapping.BankAccountSpecifier == SKS_BR_BankAccountSpecifier::All &&
            dupSKS_BR_FileFormatTranMapping.RecId != SKS_BR_FileFormatTranMapping.RecId;

        if(dupSKS_BR_FileFormatTranMapping)
        {
            error("@SKS2338");
            ret = false;
        }
    }

    return ret;
}

public boolean validateBankTranType()
{
    BankTransType transType;
    SKS_BR_GLMappingRuleGroup mappingRuleGroup;
    boolean ret = true;

    select firstonly LedgerDimension from transType where transType.BankTransType == SKS_BR_FileFormatTranMapping.BankTranType;

    if(!isCalledFromCCReconciliation)       //SKS_VSTS_14315--->
    {
        select firstonly RecId, GLMatchOrCreate from mappingRuleGroup where
            mappingRuleGroup.RuleGroupId == SKS_BR_FileFormatTranMapping.GLMappingRuleGroupId &&
            mappingRuleGroup.CCR_IsCCReconciliation == NoYes::No;
    }
    else
    {
        select firstonly RecId, CCR_GLMatchOrCreate from mappingRuleGroup where
            mappingRuleGroup.RuleGroupId == SKS_BR_FileFormatTranMapping.GLMappingRuleGroupId &&
            mappingRuleGroup.CCR_IsCCReconciliation == NoYes::Yes;
    }
    //SKS_VSTS_14315<---

    if(!isCalledFromCCReconciliation)       //SKS_VSTS_14315--->
    {
        if(mappingRuleGroup.RecId &&
           mappingRuleGroup.GLMatchOrCreate == SKS_BR_GLMatchOrCreate::GLCreate &&   //SKS_VSTS_14315
            transType.LedgerDimension == 0)
        {
            ret = false;
            error("@SKS:SKGBankTranNoMainAccount");
        }
    }
    else
    {
        if(mappingRuleGroup.RecId &&
           mappingRuleGroup.CCR_GLMatchOrCreate == SKS_CCR_GLMatchOrCreate::GLCreate &&   //SKS_VSTS_14315
            transType.LedgerDimension == 0)
        {
            ret = false;
            error("@SKS:SKGBankTranNoMainAccount");
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = element.validateBankAccountSpecifier();
    ret = element.validateBankTranType() && ret;

    if(ret)
    {
        ret = super();
    }

    return ret;
}

