/// </summary>
public void initValue()
{
    SKS_BR_FileFormatTranMapping.CCR_IsCCRFormat = isCalledFromCCReconciliation;

    super();
}

public boolean validate()
{
    boolean ret = true;

    ret = element.validateBankAccountSpecifier();

    if(ret)
    {
        ret = element.confirmBankSpecifier();
    }

    if(ret)
    {
        ret = super();
    }

    return ret;
}

public void modified()
{
    super();

    element.enableDisableBankAccountButton();
}

public void modified()
{
    super();

    if(mainFileFormat.DebitCreditIdentifier == NoYes::Yes && mainFileFormat.FileType == SKS_BR_FileType::BAI2)
    {
        SKS_BR_FileFormatTranMapping.BankDebitCredit =
            SKS_BR_BAI2Reader::getTranTypeCreditDebitFlag(str2int(SKS_BR_FileFormatTranMapping.FileTranType),
            mainFileFormat.FormatID);
    }
}

public void modified()
{
    super();

    element.enableDisableDimension();
}

