public boolean modified()
{
    boolean ret;

    ret = super();
    switch (SKS_BR_FileFormatTranMapping.CAMT053_TransGranularity)
    {
        case SKS_BR_CAMT053_TransGranularity::SKGCAMT053TranGranulaityTxAmt :
            SKS_BR_FileFormatTranMapping.CAMT053_TransGranularityXMLPaths = SKS_BR_CAMT053_TransGranularityXMLPaths::SKGCAMT053TranGranulaityXMLTxAmt;
            break;
        case SKS_BR_CAMT053_TransGranularity::SKGCAMT053TranGranularityBtch:
            SKS_BR_FileFormatTranMapping.CAMT053_TransGranularityXMLPaths = SKS_BR_CAMT053_TransGranularityXMLPaths::SKGCAMT053TranGranularityXMLBtch;
            break;
        case SKS_BR_CAMT053_TransGranularity::SKGCAMT053TranGranularityEntry:
            SKS_BR_FileFormatTranMapping.CAMT053_TransGranularityXMLPaths = SKS_BR_CAMT053_TransGranularityXMLPaths::SKGCAMT053TranGranularityXMLEntry;
            break;
        case SKS_BR_CAMT053_TransGranularity::SKGCAMT053TranGranulartityTxDtlsAmt:
            SKS_BR_FileFormatTranMapping.CAMT053_TransGranularityXMLPaths = SKS_BR_CAMT053_TransGranularityXMLPaths::SKGCAMT053TranGranulartityXMLTxDtlsAmt;
            break;
    }

    return ret;
}
