public display container showMatchIndicatorIcon(SKS_BR_BankTran _table)
{
    str                 icon;
    #ResAppl

    switch (_table.MatchIndicator)
    {
        case SKS_BR_MatchIndicator::Red :
            icon = #RedSquare;
            break;

        case SKS_BR_MatchIndicator::Green :
            icon = #GreenSquare;
            break;

        case SKS_BR_MatchIndicator::Blue :
            icon = #BlueSquare;
            break;

        case SKS_BR_MatchIndicator::Yellow :
            icon = #YellowSquare;
            break;
    }

    return ImageReference::constructForSymbol(icon).pack();
}

public int active()
{
    int ret;

    ret = super();

    //SKS_Bug_8692 -->
    if (SKS_BR_BankTran.GLMatchOrCreate == SKS_BR_GLMatchOrCreate::CustomerSettlement
        || SKS_BR_BankTran.GLMatchOrCreate == SKS_BR_GLMatchOrCreate::Manual)
    {
        SKS_BR_BankTran_ds.object(fieldNum(SKS_BR_BankTran, CustCompany)).allowEdit(true);
        if(SKS_BR_BankTran.CustCompany)
        {
            SKS_BR_BankTran_ds.object(fieldNum(SKS_BR_BankTran, CustAccount)).allowEdit(true);
        }
        else
        {
            SKS_BR_BankTran_ds.object(fieldNum(SKS_BR_BankTran, CustAccount)).allowEdit(false);
            SKS_BR_BankTran.CustAccount = '';
        }
    }
    else
    {
        SKS_BR_BankTran_ds.object(fieldNum(SKS_BR_BankTran, CustAccount)).allowEdit(false);
        SKS_BR_BankTran_ds.object(fieldNum(SKS_BR_BankTran, CustCompany)).allowEdit(false);
    }
    //SKS_Bug_8692 <--
    // SKS_BR_BlockInUse -->
    SKS_BR_BankTran_InUse.enabled(SKS_BR_BankTran.InUse);

    element.enableButtons();
    //SKS_BR_BlockInUse <--

    return ret;
}

public void executeQuery()
{
    //SKS CR 9729 -->
    query = this.query() ;
    query.allowCrossCompany(true);
    query.clearCompanyRange();
    SKS_BR_MatchView locSKS_BR_MatchView;
    str locStrSKS_BR_MatchView = AllMatched.valueStr();
    AllNotReconciled locAllNotReconciled;
    str locStrAllNotReconciled = AllReconciled.valueStr();

    locSKS_BR_MatchView = str2Enum(locSKS_BR_MatchView, locStrSKS_BR_MatchView);
    locAllNotReconciled = str2Enum(locAllNotReconciled, locStrAllNotReconciled);

    if(!bankRecCompanies)
    {
        bankRecCompanies = SKS_EB_VirtualCompany::getBankRecCompanies();
    }

    query.dataSourceTable(tableNum(SKS_BR_BankTran)).clearRange(fieldNum(SKS_BR_BankTran,AccountId));
    query.dataSourceTable(tableNum(SKS_BR_BankTran)).clearRange(fieldNum(SKS_BR_BankTran, eBankRecCustGroupId));//SKS CR 9729
    query.dataSourceTable(tableNum(SKS_BR_BankTran)).clearRange(fieldNum(SKS_BR_BankTran,OriginalStatementRecId));
    query.dataSourceTable(tableNum(SKS_BR_BankTran)).clearRange(fieldNum(SKS_BR_BankTran,Matched));
    //SKS CR 9729 <--
    qbdsBankAcctStatement.clearRanges();
    if(blankData)
    {
        query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran,OriginalStatementRecId)).value(queryValue(-1));
    }
    else if (SKS_BR_BankAcctStatement.valueStr() != '')
    {
        // statement buffer updated in SKS_BR_BankAcctStatement.modified() method
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, AccountId)).value(queryValue(statement.AccountId));
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, RecId)).value(queryValue(Statement.RecId));
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, AccountStatementNum)).value(queryValue(Statement.AccountStatementNum));
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, AccountStatementDate)).value(queryValue(statement.AccountStatementDate));
    }
    else
    {
        // 8702 CR -->
        if(BankAccountStatement_dataAreaId.valueStr() != '' || BankAccountStatement_AccountId.valueStr() != '' || eBankRecCustGroupId.valueStr() != '')
        {
            if(BankAccountStatement_dataAreaId.valueStr() != '')
            {
                query.addCompanyRange(BankAccountStatement_dataAreaId.valueStr());
            }
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran,AccountId)).value(BankAccountStatement_AccountId.valueStr());
            //SKS CR 9729
          /*  if (eBankRecCustGroupId.valueStr() != '')
            {
                query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran, eBankRecCustGroupId)).value(eBankRecCustGroupId.valueStr());
            } */
            //SKS CR 9729
        }
        else
        {
            for(int i = 1;i <= conLen(bankRecCompanies);i++)
            {
                query.addCompanyRange(conPeek(bankRecCompanies, i));
            }
        }
        // 8702 CR <--
    }

    if(!blankData)
    {
        //SKS CR 9729 -->
        if (eBankRecCustGroupId.valueStr() != '')
        {
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran, eBankRecCustGroupId)).value(eBankRecCustGroupId.valueStr());
        }
        //SKS CR 9729 <--



        if(locSKS_BR_MatchView == SKS_BR_MatchView::All)
        {
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).addSortField(fieldNum(SKS_BR_BankTran,Matched),SortOrder::Ascending); // 8702 CR
        }
        else if(locSKS_BR_MatchView == SKS_BR_MatchView::Matched)
        {
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).sortClear();
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran, Matched)).value(queryValue(NoYes::Yes));
        }
        else if(locSKS_BR_MatchView == SKS_BR_MatchView::NotMatched)
        {
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).sortClear();
            query.dataSourceTable(tableNum(SKS_BR_BankTran)).addRange(fieldNum(SKS_BR_BankTran, Matched)).value(queryValue(NoYes::No));
        }
    }       

    if (locAllNotReconciled == AllNotReconciled::Reconciled)
    {
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, ReconcileDate)).value('!'+queryValue(dateNull()));
    }
    else if (locAllNotReconciled == AllNotReconciled::NotReconciled)
    {
        qbdsBankAcctStatement.addRange(fieldNum(BankAccountStatement, ReconcileDate)).value(queryValue(dateNull()));
    }

    // Initially the form is loaded without data
    // The user will need to click Apply filter to load data
    // The queryData variable is set to true the first time Apply filter is clicked
    if(queryData)
    {
        super();    
    }
}

/// <summary>
/// Get currencyCode
/// </summary>

public display CurrencyCode currencyCode(SKS_BR_BankTran _SKS_BR_BankTran)
{
    BankAccountStatement    bankAccountStatementLoc;

    select firstonly CurrencyCode from bankAccountStatementLoc where bankAccountStatementLoc.RecId == _SKS_BR_BankTran.OriginalStatementRecId;
    return bankAccountStatementLoc.CurrencyCode;
}

/// <summary>
/// Initialize query build ds /range variables
/// </summary>
public void init()
{
    super();

    qbdsBankTran = this.query().dataSourceTable(tableNum(SKS_BR_BankTran));
    qbdsBankTran.joinMode(JoinMode::InnerJoin);
    qbdsBankAcctStatement = qbdsBankTran.addDataSource(tableNum(BankAccountStatement));
    qbdsBankAcctStatement.addLink(fieldNum(SKS_BR_BankTran, OriginalStatementRecId), fieldNum(BankAccountStatement, RecId));
}

/// <summary>
/// To enable or disable the reverse posting button.
/// </summary>
// CR-9541
public void selectionChanged()
{
    int                     markedRecord;
    BankAccountStatement    locBankAccountStatement;
    SKS_BR_BankTran         sKS_BR_BankTranLoc;
    MultiSelectionHelper    selectionHelper = MultiSelectionHelper::construct();

    super();
    selectionHelper.parmDataSource(SKS_BR_BankTran_DS);
    sKS_BR_BankTranLoc  = selectionHelper.getFirst();
    markedRecord        = SKS_BR_BankTran_DS.recordsMarked().lastIndex();
    while (sKS_BR_BankTranLoc)
    {
        select firstonly crosscompany RecId from locBankAccountStatement
            where locBankAccountStatement.RecId         == sKS_BR_BankTranLoc.OriginalStatementRecId &&
                locBankAccountStatement.ReconcileDate   == dateNull() &&
                locBankAccountStatement.DataAreaId      == sKS_BR_BankTranLoc.DataAreaId;

        if(sKS_BR_BankTranLoc.Voucher && sKS_BR_BankTranLoc.Matched && locBankAccountStatement && SKS_BR_BankTran.GLMatchOrCreate != SKS_BR_GLMatchOrCreate::DDReversal)
        {
            RevPost.enabled(true);
        }
        else
        {
            RevPost.enabled(false);
            break;
        }
        sKS_BR_BankTranLoc = selectionHelper.getNext();
    }

}

public void modified()
{
    super();

    SKS_BR_BankTran_InUse.enabled(SKS_BR_BankTran.InUse);
}

