/// <summary>
/// clicked method
/// </summary>
public void clicked()
{
    BankAccountStatement locBankAccountStatement;
    SKS_BR_BankTran lSKS_BR_BankTran;
    super();
    if(BankAccountStatement1)
    {
        if(BankAccountStatement1.ReconcileDate == dateNull())
        {
            if (Box::yesNo("@SKS:SKGCCRStmntDeletion",DialogButton::Yes,element.design().caption()) == DialogButton::Yes)
            {
                changeCompany(BankAccountStatement1.company())
                {
                    if(BankAccountStatement1.ReconcileDate != dateNull())
                    {
                        info(strFmt("@SKS:SKGBRNoDelReconciledStmt"
                        ,BankAccountStatement1.company(), BankAccountStatement1.AccountId, BankAccountStatement1.AccountStatementNum, BankAccountStatement1.AccountStatementDate));
                        return;
                    }
                    //Check to see if any of our statement's s imported transactions have been used on other statements that are reconciled
                    locBankAccountStatement = null;
                    lSKS_BR_BankTran = null;
                    Select firstonly Accountid, AccountStatementDate, AccountStatementNum, DataAreaId, Recid from locBankAccountStatement
                        where locBankAccountStatement.ReconcileDate != dateNull()
                        join lSKS_BR_BankTran
                        where lSKS_BR_BankTran.AccountId == locBankAccountStatement.AccountId
                            && lSKS_BR_BankTran.AccountStatementNum == locBankAccountStatement.AccountStatementNum
                            && lSKS_BR_BankTran.AccountStatementDate == locBankAccountStatement.AccountStatementDate
                            && lSKS_BR_BankTran.OriginalStatementRecId == BankAccountStatement1.RecId;
                    if(locBankAccountStatement)
                    {
                        info(strFmt("@SKS:SKGCCRCantDeleteImportStmt"
                        ,locBankAccountStatement.company(), locBankAccountStatement.AccountId, locBankAccountStatement.AccountStatementNum, locBankAccountStatement.AccountStatementDate));
                        return;
                    }

                    ttsBegin;
                    while select AccountId, AccountStatementDate, AccountStatementNum, ManualReconciliationID from lSKS_BR_BankTran
                            group by lSKS_BR_BankTran.AccountId, lSKS_BR_BankTran.AccountStatementDate,
                                lSKS_BR_BankTran.AccountStatementNum,    lSKS_BR_BankTran.ManualReconciliationID
                            where lSKS_BR_BankTran.AccountId == BankAccountStatement1.AccountId
                                && lSKS_BR_BankTran.AccountStatementDate == BankAccountStatement1.AccountStatementDate
                                && lSKS_BR_BankTran.AccountStatementNum == BankAccountStatement1.AccountStatementNum
                                && lSKS_BR_BankTran.ManualReconciliationID != emptyGuid()
                    {
                        SKS_BR_Matching::removeGuidMatch(lSKS_BR_BankTran.ManualReconciliationID);
                    }
                    while select AccountId, AccountStatementDate, AccountStatementNum, ManualReconciliationID from lSKS_BR_BankTran
                            group by lSKS_BR_BankTran.AccountId, lSKS_BR_BankTran.AccountStatementDate,
                                lSKS_BR_BankTran.AccountStatementNum,    lSKS_BR_BankTran.ManualReconciliationID
                            where lSKS_BR_BankTran.OriginalStatementRecId == BankAccountStatement1.RecId
                                && lSKS_BR_BankTran.ManualReconciliationID != emptyGuid()
                    {
                        SKS_BR_Matching::removeGuidMatch(lSKS_BR_BankTran.ManualReconciliationID);
                    }
                    ttscommit;
                    ttsbegin;
                    delete_from lSKS_BR_BankTran
                            where lSKS_BR_BankTran.AccountId == BankAccountStatement1.AccountId
                                && lSKS_BR_BankTran.OriginalStatementRecId == BankAccountStatement1.RecId;
                    ttscommit;
                    BankAccountStatement1.delete();
                }
                SKS_BR_BankAcctStatement.text('');
                element.doRefresh();
            }
        }

    }
}

