/// <summary>
/// Reverse the posted bank transactions.
/// </summary>
// CR-9541
public void clicked()
{
    SKS_BR_BankTran         sKS_BR_BankTranLoc;
    int                     cnt = 0;
    int                     markedRecord;
    LedgerJournalNameId     revJournalName;
    LedgerJournalName       ledgerJournalName;
    NumberSequenceTable     numberSequenceTable;
    Voucher                 nextRec;
    LedgerJournalTable      header;
    NumberSeq               numSeq;
    NumberSeqScope          locNumberSeqScope;
    SKS_PP_Setup            sKS_PP_Setup;
    boolean                 autoPost;
    int                     position;
    container               companies;
    int                     conComp;
    Map                     insertCompanydetails;
    SKS_BR_BankTran         lSKS_BR_BankTran;
    Voucher                 origBankVoucher;
    MapIterator             mi;
    sks_lb_journalarapplic	sks_lb_journalarapplic;
    insertCompanydetails = new Map(Types::String, Types::Record);

    MultiSelectionHelper    selectionHelper = MultiSelectionHelper::construct();
    selectionHelper.parmDataSource(SKS_BR_BankTran_ds);
    sKS_BR_BankTranLoc  = selectionHelper.getFirst();
    markedRecord        = SKS_BR_BankTran_ds.recordsMarked().lastIndex();

    position = SKS_BR_BankTran_ds.getPosition();

    if (Box::yesNo("@SKS:SKSYesNoReversalConfirm", DialogButton::Yes))
    {
        LedgerJournalTrans lLedgerJournalTrans;
        TransDate latestDate;

        while (sKS_BR_BankTranLoc)
        {
            // find GL journal in created company
            container conCompany = [sKS_BR_BankTranLoc.GLCreationCompany];

            select firstonly crosscompany : conCompany
                TransDate
             from lLedgerJournalTrans
            where lLedgerJournalTrans.JournalNum == sKS_BR_BankTranLoc.PaymentJournalNum
               && lLedgerJournalTrans.Voucher == sKS_BR_BankTranLoc.Voucher;

            if (lLedgerJournalTrans.TransDate > latestDate)
            {
                latestDate = lLedgerJournalTrans.TransDate;
            }

            sKS_BR_BankTranLoc = selectionHelper.getNext();
        }

        TransDate reversalDate = SKS_PP_Sys::getReversalDate(latestDate);

        sKS_BR_BankTranLoc  = selectionHelper.getFirst();

        while (sKS_BR_BankTranLoc)
        {
            cnt++;
            if(SKS_BR_Matching::ValidateReversal(sKS_BR_BankTranLoc))
            {
                if(insertCompanydetails.exists(sKS_BR_BankTranLoc.company()))
                {
                    header = insertCompanydetails.lookup(sKS_BR_BankTranLoc.company());
                }
                changecompany(sKS_BR_BankTranLoc.company())
                {
                    lSKS_BR_BankTran = sKS_BR_BankTranLoc;
                    sKS_PP_Setup = SKS_BR_BankTran::findBankRevSetup();
                    revJournalName = sKS_PP_Setup.BRBankRevJournalName;
                    ledgerJournalName = LedgerJournalName::find(revJournalName);
                    if(!revJournalName)
                    {
                        concomp = conFind(companies,sKS_BR_BankTranLoc.company());
                        if(!conComp)
                        {
                            companies = conins(companies, cnt, sKS_BR_BankTranLoc.company());
                        }
                        break;
                    }
                    else
                    {
                        select firstonly NumberSequence, NumberSequenceScope from numberSequenceTable
                                where numberSequenceTable.RecId == ledgerJournalName.NumberSequenceTable;
                        ttsbegin;
                        locNumberSeqScope = NumberSeqScope::find(numberSequenceTable.NumberSequenceScope);
                        numSeq = numberseq::newGetNumFromCode(numberSequenceTable.NumberSequence, locNumberSeqScope);
                        nextRec = numSeq.num();
                        ttscommit;

                        header = SKS_EFT_Reversal::bankReversal(sKS_BR_BankTranLoc , header, nextRec, revJournalName, reversalDate);

                        insertCompanydetails.insert(sKS_BR_BankTranLoc.company(), header);
                        header = null;
                        origBankVoucher = sKS_BR_BankTranLoc.Voucher;
                        SKS_BR_Matching::breakExistingPrematch(sKS_BR_BankTranLoc.RecId, SKS_BR_ClearPrematches::ClearSKS_BR_BankTran);
                        ttsbegin;
                        lSKS_BR_BankTran.selectForUpdate(true);
                        lSKS_BR_BankTran.reread();
                        lSKS_BR_BankTran.OrigBankVoucher = origBankVoucher;
                        lSKS_BR_BankTran.RevVoucher = nextRec;
                        lSKS_BR_BankTran.update();
                        ttscommit;
                    }
                    delete_from sks_lb_journalarapplic where SKS_lb_journalarapplic.sks_br_banktran == sKS_BR_BankTranLoc.RecId;
                }
                SKS_LB_JournalARApplic::updateRevCustVendOpenTrans(sKS_BR_BankTranLoc.RecId);
            }
            sKS_BR_BankTranLoc = selectionHelper.getNext();
        }
        if(markedRecord <=  cnt)
        {
            mi = new MapIterator(insertCompanydetails);

            while (mi.more())
            {
                header = mi.value();
                changecompany(header.company())
                {
                    sKS_PP_Setup   = SKS_BR_BankTran::findBankRevSetup();
                    autoPost       = sKS_PP_Setup.BRAutoPostReversalJournal;
                    SKS_EFT_Reversal::AutoPostJournal(autoPost, header);
                }
                mi.next();
            }
            BankAccountStatement_ds.research();
            BankAccountStatement1_ds.research();
            SKS_BR_BankTran_ds.research(true);
            SKS_BR_BankTran_ds.setPosition(position);
        }
        for(int i= 1;i<=conLen(companies);i++)
        {
            error(strFmt("@SKS:SKSErrorBankRevSetup",conPeek(companies,i)));
        }
    }
}

