[Form]
public class SKS_BR_StatmentReconciliationRules extends FormRun
{
    SKS_BR_BankTran     SKS_BR_BankTranLoc;
    QueryBuildRange     queryRange;
    RefRecId            sks_BR_BankTranRecId; //9479
    RefRecId            sks_CCR_BankTranRecId; //SKS_FDD_13903
    SKS_CCR_BankTran     SKS_CCR_BankTranLoc;  //SKS_FDD_13903
    boolean isCalledFromCCReconciliation;// SKS_FDD_13903
    QueryBuildRange     queryRangeCCR;// SKS_FDD_13903

}

public void init()
{
    // SKS_FDD_13903    Begin
    Args args = element.args();
    if(args.caller().name() == menuItemDisplayStr(SKS_CCR_StatementReconciliation))
    {
        isCalledFromCCReconciliation = true;
    }

    if(!isCalledFromCCReconciliation)
    {

        SKS_BR_BankTranLoc = element.args().record() as SKS_BR_BankTran;
        sks_BR_BankTranRecId = str2Int64(element.args().parm()); //9479
    }
    else    //SKS_FDD_13903     End
    {
        SKS_CCR_BankTranLoc = element.args().record() as SKS_CCR_BankTran;
        sks_CCR_BankTranRecId = str2Int64(element.args().parm());
    }
    super();

}

/// <summary>
/// SKS_FDD_13903    Begin
/// </summary>
public void enableDisable()
{
    if(isCalledFromCCReconciliation)
    {
        MatchingRuleTab.visible(false);
        GLMappingRuleTab.visible(false);
        ParseRuleTab.visible(false);
        CustomersettlementTab.visible(false);
    }
    else
    {
        SKS_BR_BankTran lSKS_BR_BankTran;

        select firstonly lSKS_BR_BankTran
            where lSKS_BR_BankTran.RecId == sks_BR_BankTranRecId;

        CCR_MatchingRuleTab.visible(false);
        CCR_GLMappingRuleTab.visible(false);
        CCR_ParseRuleTab.visible(false);

        if(lSKS_BR_BankTran.IsManual)
        {
            GLMappingRule_JournalName.visible(false);
            GLMappingRule_ManualJournalName.visible(true);
        }
        else
        {
            GLMappingRule_JournalName.visible(true);
            GLMappingRule_ManualJournalName.visible(false);
        }
    }
}// SKS_FDD_13903    End

/// <summary>
/// init method
/// </summary>
public void init()
{
    super();
    this.query().dataSourceName(identifierStr(SKS_BR_BankTran)).clearDynalinks();
    queryRange = this.query().dataSourceName(identifierStr(SKS_BR_BankTran)).addRange(fieldnum(SKS_BR_BankTran,RecId));
}

/// <summary>
/// SKS_FDD_13903    Begin
/// </summary>
/// <returns>int</returns>
public int active()
{
    int ret;

    ret = super();

    element.enableDisable();

    return ret;
}// SKS_FDD_13903    End

