/// <summary>
/// executeQuery method
/// </summary>
public void executeQuery()
{
    this.query().dataSourceName(identifierStr(SKS_CCR_BankTran)).clearDynalinks();
    queryRangeCCR.value(queryValue(sks_CCR_BankTranRecId));
    super();
}

/// <summary>
/// init method
/// </summary>
public void init()
{
    super();
    this.query().dataSourceName(identifierStr(SKS_CCR_BankTran)).clearDynalinks();
    queryRangeCCR = this.query().dataSourceName(identifierStr(SKS_CCR_BankTran)).addRange(fieldnum(SKS_CCR_BankTran,RecId));
}

/// <summary>
/// jumpRef method
/// </summary>
/// SKS_FDD_13903   Begin
public void jumpRef()
{
    MenuFunction menuFunction;
    Args         args = new args();

    if(isCalledFromCCReconciliation)
    {
        args.record(SKS_CCR_BankTran);

        args.caller(element);

        menuFunction = new MenuFunction(menuitemDisplayStr(SKS_CCR_GLMappingRuleGroup), MenuItemType::Display);
    }
    else
    {
        args.record(SKS_BR_BankTran);

        args.caller(element);

        menuFunction = new MenuFunction(menuitemDisplayStr(SKS_BR_GLMappingRuleGroup), MenuItemType::Display);
    }

    menuFunction.run(args);
}

//SKS_FDD_13903     End

