/// <summary>
/// BugId: 7352
/// </summary>
public void jumpRef()
{
    LedgerJournalTable  ledgerJournalTable;
    LedgerJournalType   ledgerJournalType;            
    MenuFunction menuFunction;// = new MenuFunction(menuitemDisplayStr(LedgerJournalTable), MenuItemType::Display);
    Args         args;// = new args();

    select firstonly crosscompany  ledgerJournalTable
        where ledgerJournalTable.JournalNum == SKS_BR_BankTran.PaymentJournalNum
            && ledgerJournalTable.DataAreaId == SKS_BR_BankTran.GLCreationCompany; // SKS_Bug_10847
    ledgerJournalType = LedgerJournalType::Daily;

    appl.setDefaultCompany(ledgerJournalTable.company());
    menuFunction = new MenuFunction(menuitemDisplayStr(LedgerJournalTable), MenuItemType::Display);
    args = new args();
    args.caller(this.formRun());
    //args.parmEnum(ledgerJournalType);
    args.parmEnumType(enumNum(LedgerJournalType));
    args.lookupRecord(ledgerJournalTable);
    menuFunction.run(args);
}

