    }

}

display container statusIcon(SKS_BR_BankTran bankTran)
{
    ImageReference image;
    container imageContainer;

    if( bankTran.IntermMatch == NoYes::Yes || bankTran.LedgerJournalRecId/*GL CREATED*/)
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::Checkmark, "@SKS844");
    }
    else if(bankTran.MultipleMatches)
    {
        image = ImageReference::constructForSymbol(ImageReferenceSymbol::HighPriority, "@SKS874");
    }
    else
    {
        image = ImageReference::constructForSymbol(''); //no symbol
    }

    imageContainer = image.pack();

    return imageContainer;

}

public int active()
{
    int ret;
    SKS_PP_Bank locSKS_PP_Bank;

    ret = super();

    element.setTabInfo();

    btnDuplicate.enabled(false);

    if(SKS_BR_BankTran.ManualReconciliationID == str2guid(#EmptyGuidString) && SKS_BR_BankTran.LedgerJournalRecId == 0 && SKS_BR_BankTran.PaymentJournalNum == '')
    {
        btnRemoveMatch.enabled(false);
        SKS_BR_BankTran_BookNum.enabled(true);
        SKS_BR_BankTran_ds.allowDelete(true);

        if(SKS_BR_BankTran.MultipleMatches)
        {
            btnDuplicate.enabled(true);
        }
    }
    else
    {
        if(SKS_BR_BankTran.LedgerJournalRecId != 0)
        {
            btnRemoveMatch.enabled(false);
        }
        else
        {
            btnRemoveMatch.enabled(true);
        }
        SKS_BR_BankTran_BookNum.enabled(false);
        SKS_BR_BankTran_ds.allowDelete(false);
    }
    //enable Create GL button if:
    //1:  Buttons are enabled
    //2:  SKS_BR_BankTran is NOT matched
    //3:  not setup for bank rec locSKS_PP_Bank.BRFormatID(ZBA accounts should NOT have a FormatId as they are NOT setup)
    //4:  Have not already created GL
    select BRFormatID from locSKS_PP_Bank where locSKS_PP_Bank.BankAccountId == SKS_BR_BankTran.AccountId;
    btnCreateGl.enabled(SKS_BR_BankTran.ManualReconciliationID == str2guid(#EmptyGuidString)
                        && locSKS_PP_Bank.BRFormatID
                        && SKS_BR_BankTran.LedgerJournalRecId == 0);

    return ret;
}

public void executeQuery()
{
    QueryBuildRange qbr;
    QueryBuildDataSource    queryBuildDataSource;
    Container companies;
    Counter conCount;
    //SKS_8688CR -->
    RecIdCount      cntAutoCreateBankStmt;
    RecIdCount      cntTotalBankAccount;
    SKS_PP_Bank     sKS_PP_Bank;
    //SKS_8688CR <--
    ;

    queryBuildDataSource = this.query().dataSourceTable(tablenum(SKS_BR_BankTran));
    queryBuildDataSource.clearRanges();
    queryBuildDataSource.addRange(fieldNum(SKS_BR_BankTran, OriginalStatementRecId)).value('0');//SKS_8688CR
    // Restrict companies
    companies = SKS_EB_VirtualCompany::getBankRecCompanies();
    for(conCount = 1; conCount <= conlen(companies);conCount++)
    {
        this.query().addCompanyRange(conpeek(companies, conCount));
    }
    //SKS_8688CR -->
    select crossCompany:companies count(RecId) from sKS_PP_Bank
        where sKS_PP_Bank.BRCreateBankStatements == true &&
            sKS_PP_Bank.BRFormatID != '';

    cntAutoCreateBankStmt = sKS_PP_Bank.RecId;

    select crossCompany:companies count(RecId) from sKS_PP_Bank
         where sKS_PP_Bank.BRFormatID != ''; 

    cntTotalBankAccount = sKS_PP_Bank.RecId;

    if(cntAutoCreateBankStmt == cntTotalBankAccount)
    {
        info("@SKS6192");
    }
    //SKS_8688CR <--
    if(ViewOptions.selection() == 1)//Transaction Viewing Options:  All
    {
        qbr = queryBuildDataSource.addRange(
            fieldNum(SKS_BR_BankTran, ManualReconciliationID));
        qbr.status(RangeStatus::Hidden);

        qbr.value(strFmt('((ManualReconciliationID == "%1") || ((ManualReconciliationID != "%1") && (IntermMatch == %2)))',
                    str2guid(#EmptyGuidString), any2int(NoYes::Yes)));
    }
    else if(ViewOptions.selection() == 0) // Transaction Viewing Options:  Un-matched
    {
        qbr = queryBuildDataSource.addRange(
            fieldNum(SKS_BR_BankTran, ManualReconciliationID));
        qbr.value(guid2str(nullGuid));
        qbr.status(RangeStatus::Hidden);

        qbr = queryBuildDataSource.addRange(
            fieldNum(SKS_BR_BankTran, LedgerJournalRecId));
        qbr.value('0');
        qbr.status(RangeStatus::Hidden);
    }

    super();
}

