void clicked()
{
    FormRun formRun;
    Args args;

    if(!SKS_BR_BankTran)
    {
        return;
    }

    changeCompany(SKS_BR_BankTran.company())
    {
        // Launch form modally
        args = new Args();

        if(SKS_BR_BankTran.MatchedType == SKS_BR_MatchedType::JournalSummary)
        {
            args.name(formstr(SKS_BR_MatchingDuplicateJournal));
        }
        else
        {
            args.name(formstr(SKS_BR_MatchingDuplicate));
        }

        args.caller(element);
        args.record(SKS_BR_BankTran);
        formRun = classFactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait(true);
    }
}

