[Form]
public class SKS_CC_CreditCardInvoiceIssues extends FormRun
{
    QueryBuildRange salesIdRange;

}

public void init()
{
    super();

    QueryBuildRange invoiceErrorAfterCaptureRange = this.query().dataSourceTable(tableNum(SalesTable)).addRange(fieldNum(SalesTable, SKS_CC_InvoiceErrorAfterCapture));
    invoiceErrorAfterCaptureRange.value(SysQuery::range(SKS_CC_InvoiceErrorAfterCCCaptureSuccess::CaptureVoided, SKS_CC_InvoiceErrorAfterCCCaptureSuccess::CaptureVoidFailed));
    invoiceErrorAfterCaptureRange.status(RangeStatus::Hidden);

    QueryBuildRange salesStatusRange = this.query().dataSourceTable(tableNum(SalesTable)).addRange(fieldNum(SalesTable, SalesStatus));
    salesStatusRange.value(strFmt('%1, %2', enum2Str(SalesStatus::Backorder), enum2Str(SalesStatus::Delivered)));
    salesStatusRange.status(RangeStatus::Hidden);
}

public void init()
{
    super();

    salesIdRange = this.query().dataSourceName('CreditCardAuthTrans').addRange(fieldnum(CreditCardAuthTrans, SalesId));
}

public void executeQuery()
{
    if (!salesTable.SalesId)
    {
        salesIdRange.value('0');
    }
    else
    {
        salesIdRange.value(salesTable.SalesId);
    }

    super();
}
