[Form]
public class SKS_CC_PayLinkQueue extends FormRun
{
    QueryBuildRange statusRange, transTypeRange;
    QueryBuildRange refRecIdRange, refTableIdRange;
    TableId callerTableId;
    Common callerRecord;
    SKS_PP_Setup sksParms;
    FormDataSource callerDataSource;
    FormRun callerFormRun;

}

private str getPropertyValue(CreditCardPaymentProperty _property)
{
    switch (_property.parmValueType())
    {
        case CreditCardPropertyDataType::DateTime:
            return strFmt('%1', _property.parmDateTimeValue());
        case CreditCardPropertyDataType::Decimal:
            return strFmt('%1', _property.parmDecimalValue());
        default:
            return _property.parmIsPassword() ? strRep('*', 10) : _property.parmStringValue();
    }
}

public int displayTableProperties(FormTableControl _table, CreditCardPaymentProperties _properties, int _currentRow)
{
    ListEnumerator enumProps = _properties.enumerator();
    CreditCardPaymentProperty property;
    int currentRow = _currentRow;

    Debug::assert(currentRow >= 1);

    while (enumProps.moveNext())
    {
        property = enumProps.current();
        if (property.parmValueType() == CreditCardPropertyDataType::PropertyList)
        {
            // Flatten sub-properties
            currentRow = element.displayTableProperties(_table, property.parmPropertyListValue(), currentRow);
        }
        else
        {
            _table.cell(1, currentRow).data(strFmt('%1.%2', property.parmNamespace(), property.parmName()));
            _table.cell(2, currentRow).data(this.getPropertyValue(property));
            currentRow++;
        }
    }

    return currentRow;
}

public void displayXmlValues(FormTableControl _table, str _xmlString)
{
    _table.clear();
    _table.columns(2);
    _table.setColLabel(1, "@SYS31438");
    _table.setColLabel(2, "@SYS71361");

    int rowCount = this.displayTableProperties(_table, CreditCardPaymentProperties::fromXmlString(_xmlString), 1);

    _table.rows(rowCount - 1);
}

private void refreshCallerForm()
{
    if (callerDataSource)
    {
        callerDataSource.research(true);
        callerDataSource.refresh();
    }
    else if (callerFormRun)
    {
        callerFormRun.doResearch();
        callerFormRun.doRefresh();

        if (element.args() && TradeFormHelper::isCalledFromForm(element.args(), formStr(SKS_CC_CustPaymTable)))
        {
            callerFormRun.setDataSourceEditOptionsFromCaller();
        }
    }
}

public void init()
{
    CustTable custTableCallerLoc;
    QueryBuildDataSource qbdsPayLinkQueue;
    QueryBuildRange qbrCustAccount;

    sksParms = SKS_PP_Setup::find();
    if (element.args() && element.args().record())
    {
        callerRecord = element.args().record();                
    }

    super();

    PayLinkQueueRefresh.visible(sksParms.CCPLEnablePayLinks || sksParms.CCPLEnableWalletLinks);
    PayLinkQueueCancel.visible(sksParms.CCPLEnablePayLinks || sksParms.CCPLEnableWalletLinks);
    PayLinkQueueResend.visible(sksParms.CCPLEnablePayLinks || sksParms.CCPLEnableWalletLinks);
    ResetInProcessStatus.visible(sksParms.CCPLEnablePayLinks || sksParms.CCPLEnableWalletLinks);

    qbdsPayLinkQueue = this.query().dataSourceTable(tableNum(SKS_CC_PayLinkQueue));
    qbdsPayLinkQueue.addOrderByField(fieldNum(SKS_CC_PayLinkQueue, SentDateTime), SortOrder::Descending);
    qbrCustAccount = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, AccountNum));
    if (callerRecord)
    {
        statusRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, Status));
        StatusFilterCtrl.selection(SKS_CC_PayLinkProcessStatus::All);                
        transTypeRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, TransType));                

        refTableIdRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, RefTableId));
        refTableIdRange.status(RangeStatus::Hidden);
        refRecIdRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, RefRecId));
        refRecIdRange.status(RangeStatus::Hidden);
        qbdsPayLinkQueue.clearDynalinks();
        qbrCustAccount.status(RangeStatus::Locked);

        switch (callerRecord.TableId) 
        {                    
            case tableNum(CreditCardCust):
                TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::WalletLink);
                CreditCardCust creditCardCustCallerLoc = callerRecord;
                if (creditCardCustCallerLoc)
                {                            
                    custTableCallerLoc = CustTable::find(creditCardCustCallerLoc.CustAccount);
                }

                qbrCustAccount.value(custTableCallerLoc.AccountNum);

                if (sksParms.CCUseOneTimeCustomerLogic == NoYes::Yes && custTableCallerLoc.OneTimeCustomer)
                {
                    refTableIdRange.value(queryValue(creditCardCustCallerLoc.SKS_CC_OneTimeCustRefTableId));
                    refRecIdRange.value(queryValue(creditCardCustCallerLoc.SKS_CC_OneTimeCustRefRecId));
                }
                break;
            case tableNum(CustTable):
                if (element.args().parm() == enum2Str(SKS_CC_PayLinkTransactionType::WalletLink))
                {
                    TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::WalletLink);
                }

                custTableCallerLoc = callerRecord;
                qbrCustAccount.value(custTableCallerLoc.AccountNum);
                if (sksParms.CCUseOneTimeCustomerLogic == NoYes::Yes && custTableCallerLoc.OneTimeCustomer)
                {
                    // Value below forced to (-1) to ensure no records are returned
                    refTableIdRange.value(queryValue(-1));
                    refRecIdRange.value(queryValue(-1));
                }

                if (TradeFormHelper::isCalledFromForm(element.args(), formStr(CreditCardCust)))
                {
                    callerFormRun = element.args().caller();
                }
                break;
            case tableNum(SalesTable):
                TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::PayLink);
                SalesTable salesTableCallerLoc = callerRecord;
                if (salesTableCallerLoc)
                {               
                    qbrCustAccount.value(salesTableCallerLoc.InvoiceAccount);
                    if (sksParms.CCUseOneTimeCustomerLogic == NoYes::Yes && salesTableCallerLoc.custTable_InvoiceAccount().OneTimeCustomer)
                    {
                        refTableIdRange.value(queryValue(salesTableCallerLoc.TableId));
                        refRecIdRange.value(queryValue(salesTableCallerLoc.RecId));
                    }
                    else 
                    {
                        refTableIdRange.value(strFmt('%1, 0', salesTableCallerLoc.TableId));
                        refRecIdRange.value(strFmt('%1, 0', salesTableCallerLoc.RecId));
                    }

                    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(SalesTable)))
                    {
                        callerDataSource = element.args().record().dataSource();
                    }
                }
                break;
            case tableNum(LedgerJournalTrans):
                TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::PayLink);
                LedgerJournalTrans ledgerJournalTransLoc = callerRecord;                        
                CustAccount custAccount;
                if (ledgerJournalTransLoc)
                {
                    custAccount = ledgerJournalTransLoc.accountDisplay();
                    qbrCustAccount.value(custAccount);
                    if (sksParms.CCUseOneTimeCustomerLogic == NoYes::Yes && CustTable::find(custAccount).OneTimeCustomer)
                    {
                        refTableIdRange.value(queryValue(ledgerJournalTransLoc.TableId));
                        refRecIdRange.value(queryValue(ledgerJournalTransLoc.RecId));
                    }
                    else
                    {
                        refTableIdRange.value(strFmt('%1, 0', ledgerJournalTransLoc.TableId));
                        refRecIdRange.value(strFmt('%1, 0', ledgerJournalTransLoc.RecId));
                    }

                    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(SKS_CC_CustPaymTable)))
                    {
                        callerFormRun = element.args().caller();                                                              
                    }
                }
                break;
            case tableNum(SKS_CC_CustPaymTable):
                TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::PayLink);
                SKS_CC_CustPaymTable custPaymTableCallerLoc = callerRecord;                        
                if (custPaymTableCallerLoc)
                {
                    qbrCustAccount.value(custPaymTableCallerLoc.CustAccount);
                    if (sksParms.CCUseOneTimeCustomerLogic == NoYes::Yes && CustTable::find(custPaymTableCallerLoc.CustAccount).OneTimeCustomer)
                    {
                        refTableIdRange.value(queryValue(custPaymTableCallerLoc.TableId));
                        refRecIdRange.value(queryValue(custPaymTableCallerLoc.RecId));
                    }
                    else
                    {
                        refTableIdRange.value(strFmt('%1, 0', custPaymTableCallerLoc.TableId));
                        refRecIdRange.value(strFmt('%1, 0', custPaymTableCallerLoc.RecId));
                    }

                    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(SKS_CC_CustPaymTable)))
                    {
                        callerDataSource = element.args().record().dataSource();
                    }
                }
                break;
            default:
                TransTypeFilterCtrl.selection(SKS_CC_PayLinkTransactionType::All);
                refTableIdRange.value(queryValue(callerRecord.TableId));
                refRecIdRange.value(queryValue(callerRecord.RecId));
                // Added for performance reason so that where clause matches index on table.
                qbrCustAccount.value(SysQuery::valueUnlimited());
                break;
        }
    }
    else
    {
        statusRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, Status));
        transTypeRange = qbdsPayLinkQueue.addRange(fieldnum(SKS_CC_PayLinkQueue, TransType));
        // Added for performance reason so that where clause matches index on table.
        qbrCustAccount.value(SysQuery::valueUnlimited());
    }
}

public void executeQuery()
{
    if (statusRange)
    {
        if (StatusFilterCtrl.selection() == SKS_CC_PayLinkProcessStatus::All)
        {
            statusRange.value(SysQuery::range(SKS_CC_PayLinkProcessStatus::Pending, SKS_CC_PayLinkProcessStatus::Canceled));
        }
        else
        {
            statusRange.value(queryValue(StatusFilterCtrl.selection()));
        }
    }

    if (transTypeRange)
    {
        if (TransTypeFilterCtrl.selection() == SKS_CC_PayLinkTransactionType::All)
        {
            transTypeRange.value(SysQuery::range(SKS_CC_PayLinkTransactionType::WalletLink, SKS_CC_PayLinkTransactionType::PayLink));
        }
        else
        {
            transTypeRange.value(queryValue(TransTypeFilterCtrl.selection()));
        }
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    element.displayXmlValues(ResultTable, SKS_CC_PayLinkQueue.PayLinkResponse);
    element.displayXmlValues(RequestTable, SKS_CC_PayLinkQueue.PayLinkRequest);

    if (sksParms.CCPLEnablePayLinks || sksParms.CCPLEnableWalletLinks)
    {
        PayLinkQueueRefresh.enabled(SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Pending ||
            SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Error);

        PayLinkQueueCancel.enabled(SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Pending ||
            SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Error);

        // PayFabric only supports resending of PayLinks from the API not WalletLinks at this time.
        // Once PayFabric adds the ability to resend WalletLinks from the API then remove condition below.
        PayLinkQueueResend.enabled((SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Pending ||
            SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Error) &&
            SKS_CC_PayLinkQueue.TransType == SKS_CC_PayLinkTransactionType::PayLink);

        ResetInProcessStatus.enabled(SKS_CC_PayLinkQueue.InProcess == NoYes::Yes &&
            (SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Pending ||
             SKS_CC_PayLinkQueue.Status == SKS_CC_PayLinkProcessStatus::Error));

    }               

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        SKS_CC_PayLinkQueue_ds.executeQuery();
    }

    return ret;
}

