void clearSelected()
{
    element.editSelectFileNumber( true, '');
    nextactiondisplay.setEditText( Enum2str(SKS_PP_NextActionDisplay::Select));
    nextactiondisplay.enabled(false);
    BeginProcessing.enabled(false);
    PrintReport.enabled(false);
}

