void setFileDetails()
{
    UTCDatetime fileDate;
    str filePathName;
    ;
    Filename.text(msks_eftap_File.FileName);
    Filepath.text(msks_eftap_File.FilePath);

    if(msks_eftap_File.FileName != '')
    {
        filePathName = msks_eftap_File.FilePath + '\\' + msks_eftap_File.FileName;
    }
    else
    {
        filePathName = msks_eftap_File.FilePath;
    }

    SKS_EB_FileHub fileHub = SKS_EB_FileHub::construct(msks_eftap_File.BFBankIntegration);
    fileHub.runFileInquire(filePathName);
    if(fileHub.attrFileExists())
    {
        fileDate = fileHub.attrFileModified();
        FileDateTime.visible(true);
        FileDateTime.dateTimeValue(fileDate);
        FileNotFound.visible(false);
        LoadFile.enabled(true);
    }
    else
    {
        FileDateTime.visible(false);
        FileNotFound.visible(true);
        LoadFile.enabled(false);
    }
}

//BP Deviation Documented
public display SKS_EB_PreNoteStatus getPrenote(SKS_EFT_FileDetail sks_eft_FileDetail)
{
    VendBankAccount vendBankAccountPN;
    Container company;
    ;
    company += sks_eft_FileDetail.AccountCompany;

    select firstonly crosscompany:company SKS_EFT_PreNoteStatus, SKS_EFT_PreNoteDate from vendBankAccountPN
        where vendBankAccountPN.VendAccount == sks_eft_FileDetail.AccountNum
            && vendBankAccountPN.AccountId == sks_eft_FileDetail.DepBankAccountId;

    DepPreNoteDate.dateValue(vendBankAccountPN.SKS_EFT_PreNoteDate);
    return vendBankAccountPN.SKS_EFT_PreNoteStatus;
}

public void executeQuery()
{
    QueryBuildRange qbrRange;
    QueryBuildDataSource qbdSource;
    ;
    this.query().dataSourceTable(tablenum(SKS_EFT_FileDetail)).clearRanges();
    qbdSource = this.query().DataSourceTable(tablenum(SKS_EFT_FileDetail));

    qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordType));
    qbrRange.value(QueryValue(SKS_EFT_RecordType::PreNote));
    qbrRange.status(RangeStatus::Hidden);

    super();
}

