            } //<--- FDD-10590

            if(lsks_eft_bank.AllowPostedPmtReversal == NoYes::Yes)
            {
                SKS_EFT_FileHistory fileHistoryTable;
                select firstonly fileHistoryTable
                    where fileHistoryTable.OutboundFileRecId == sks_eft_filedetail_remit.FileRecId
                        && fileHistoryTable.Action == SKS_EFT_ActionHistory::paymReversalCreated
                        && fileHistoryTable.OutboundFileDetailRecId == sks_eft_filedetail_remit.RecId;
