public void executeQuery()
{
    QueryBuildRange qbrRange;
    QueryBuildDataSource qbdSource;
    ;
    this.query().allowCrossCompany(true);//FDD-10590
    this.query().dataSourceTable(tablenum(SKS_EFT_FileDetail)).clearRanges();
    qbdSource = this.query().DataSourceTable(tablenum(SKS_EFT_FileDetail));

    qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordType));
    qbrRange.value(QueryValue(SKS_EFT_RecordType::Invoice));
    qbrRange.status(RangeStatus::Hidden);

    qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordSummaryRecId));
    qbrRange.value(QueryValue(0));
    qbrRange.status(RangeStatus::Hidden);

    qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordSection));
    if(msks_eftap_File.FXAProcessing)
    {
        qbrRange.value(QueryValue(SKS_EFT_RecordSection::FXAllVendor));
    }
    else
    {
        qbrRange.value(QueryValue(SKS_EFT_RecordSection::Payments));
    }
    qbrRange.status(RangeStatus::Hidden);

    super();
}

public void linkActive()
{
    ;
    super();

    //SKS_EFT_FileFormat msks_eft_FileFormat = SKS_EFT_FileFormat::findByCompany(msks_eftap_file.DataAreaId, msks_eftap_file.FormatId, true);
    //if(msks_eft_FileFormat.APIFileFormatType != SKS_EFT_APIFormatType::API)
    if(msks_eftap_File.APIFormatType != SKS_EFT_APIFormatType::API)
    {
        element.setFileDetails();
    }

    if(mSKS_EFTAP_file.journaloption == sks_eft_FileJournalOption::MasterAccountList)
    {
        MastAcctList.setFocus();
    }
    else if(mSKS_EFTAP_file.journaloption == sks_eft_FileJournalOption::PreNotesOnly)
    {
        prenotes.setFocus();
    }
}

