/// <summary>
///Used to show the PaymentStatus from LedgerJournalTrans
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
public display str paymentStatusLine(SKS_EFT_FileDetail _this)
{
    LedgerJournalTrans      ledgerJournalTransLoc;

    select firstonly crosscompany PaymentStatus from ledgerJournalTransLoc where ledgerJournalTransLoc.RecId == _this.PaymentLineRecId;

    return enum2Str(ledgerJournalTransLoc.PaymentStatus);
}

