public void executeQuery()
{
    QueryBuildRange qbrRange;
    QueryBuildDataSource qbdSource;
    ;
    this.query().dataSourceTable(tablenum(SKS_EFT_FileDetail)).clearRanges();
    qbdSource = this.query().DataSourceTable(tablenum(SKS_EFT_FileDetail));

    qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordType));
    qbrRange.value(QueryValue(SKS_EFT_RecordType::MAL));
    qbrRange.status(RangeStatus::Hidden);

    super();
}

public void executeQuery()
{
    QueryBuildRange qbrRange;
    QueryBuildDataSource qbdSource;
    ;
    if(msks_eftap_File.FXAProcessing == NoYes::Yes)
    {
        this.query().dataSourceTable(tablenum(SKS_EFT_FileDetail)).clearRanges();
        qbdSource = this.query().DataSourceTable(tablenum(SKS_EFT_FileDetail));

        qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordType));
        qbrRange.value(QueryValue(SKS_EFT_RecordType::Invoice));
        qbrRange.status(RangeStatus::Hidden);

        qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordSummaryRecId));
        qbrRange.value(QueryValue(0));
        qbrRange.status(RangeStatus::Hidden);

        qbrRange = qbdsource.addRange(fieldnum(SKS_EFT_FileDetail, RecordSection));
        qbrRange.value(QueryValue(SKS_EFT_RecordSection::Payments));//If not FXALL we dont want this query to return anything

        qbrRange.status(RangeStatus::Hidden);

        super();
    }
}

