void clicked()
{
    container               conParams, conSelectedFileDetails;
    SKS_EFT_FileDetail      sKS_EFT_FileDetail;
    MultiSelectionHelper    selectionHelper = MultiSelectionHelper::construct();
    LedgerJournalTrans  lLedgerJournalTrans;

    // 9192 CR -->
    selectionHelper.parmDataSource(SKS_EFT_Filedetail_Remit_DS);
    sKS_EFT_FileDetail = selectionHelper.getFirst();
    TransDate latestDate;

    while (sKS_EFT_FileDetail)
    {
        conSelectedFileDetails += sKS_EFT_FileDetail.RecId;

        if(sKS_EFT_FileDetail.PaymentCompany)//FDD-10590 --->
        {
            changecompany(sKS_EFT_FileDetail.PaymentCompany)
            {
                lLedgerJournalTrans = null;
                select firstOnly TransDate from lLedgerJournalTrans where lLedgerJournalTrans.RecId == sKS_EFT_FileDetail.PaymentLineRecId;
            }
        }
        else
        {
            select firstOnly TransDate from lLedgerJournalTrans where lLedgerJournalTrans.RecId == sKS_EFT_FileDetail.PaymentLineRecId;
        }

        if (lLedgerJournalTrans.TransDate > latestDate)
        {
            latestDate = lLedgerJournalTrans.TransDate;
        }

        sKS_EFT_FileDetail = selectionHelper.getNext();
    }

    TransDate reversalDate = SKS_PP_Sys::getReversalDate(latestDate);


    // run the actual job
    conParams += [conSelectedFileDetails];
    conParams += reversalDate;

    SysOperationSandbox::callStaticMethod(
                                classNum(SKS_EFTAP_ReversePostedPayment),
                                staticMethodStr(SKS_EFTAP_ReversePostedPayment, runStatic),
                                conParams,
                                '@SKS3015');

    ReversePostedPayment.enabled(false); // 9192 CR
}

