        }
        else
        {
            sks_eft_SendMail.process_EFT_eMail(msks_eftap_File.FileNbr, msks_eftap_File.EFTFileType, SKS_EFT_EmailSendType::Individual, '', SKS_EFT_FileDetail_Remit.AccountNum, NoYes::Yes);
        }
        // 9205 CR <--
    }
    else
    {
        // 9205 CR -->
        if(msks_eftap_File.EFTFileType == SKS_EFT_Module::AR)
        {
            info("@SKS:SKGInfoDDEmailBatch"); // SKS_Bug_9629
            SKS_EFTAP_CreateSendFileBatch::createMultiThreadBatch(msks_eftap_File,
                SKS_PP_NextActionDisplay::CompleteNow, SKS_EFT_EmailSendType::Individual,'', SKS_EFT_FileDetail_Remit.AccountNum, NoYes::No, SKS_EFT_FileDetail_Remit); // 9205 CR
        }
        else if(msks_eftap_File.EFTFileType == SKS_EFT_Module::AP)
        {
            info("@SKS:SKGInfoAPEmailBatch");
            SKS_EFTAP_CreateSendFileBatch::createMultiThreadBatch(msks_eftap_File,
                SKS_PP_NextActionDisplay::CompleteNow, SKS_EFT_EmailSendType::Individual,'', SKS_EFT_FileDetail_Remit.AccountNum, NoYes::No, SKS_EFT_FileDetail_Remit);
        }

        if (msks_eftap_File.FileStatus != SKS_EFT_FileStatus::TransmittedEmailPending)
        {
            msks_eftap_File.FileStatus = SKS_EFT_FileStatus::CompletedEmailPending;
            msks_eftap_File.update();
        }
        //else
        //{
        //    sks_eft_SendMail.process_EFT_eMail(msks_eftap_File.FileNbr, msks_eftap_File.EFTFileType, SKS_EFT_EmailSendType::Individual, '', SKS_EFT_FileDetail_Remit.AccountNum);
        //}
        // 9496 CR <--
    }

    SKS_EFT_FileDetail_Remit_ds.research(true);
