            }
        }
        if(isAttachmentMissed && msks_eftap_File.EFTFileType == SKS_EFT_Module::AR)
        {
            warning("@SKS:SKGWarningAttachmentMissed");
        }
    }
    // CR-9496 start
    else
    {
        if(mSKS_EB_Vendor)
        {
            sKS_APR_SRS1Attachment          = mSKS_EB_Vendor.APSRS1Attachment;
            sKS_APR_SRS1FileName            = mSKS_EB_Vendor.APSRS1FileName;
            sKS_APR_SRS1FileType            = mSKS_EB_Vendor.APSRS1FileType;
            sKS_APR_SRS2Attachment          = mSKS_EB_Vendor.APSRS2Attachment;
            sKS_APR_SRS2FileName            = mSKS_EB_Vendor.APSRS2FileName;
            sKS_APR_SRS2FileType            = mSKS_EB_Vendor.APSRS2FileType;
        }

        if((mSKS_EB_Vendor.APSRS1Attachment && mSKS_EB_Vendor.APSRS1EmailSSRS != '') ||
            (mSKS_EB_Vendor.APSRS2Attachment && mSKS_EB_Vendor.APSRS2EmailSSRS != ''))
        {
            sKS_APR_SRS1EmailSSRS           = mSKS_EB_Vendor.APSRS1EmailSSRS;
            sKS_APR_SRS2EmailSSRS           = mSKS_EB_Vendor.APSRS2EmailSSRS;
        }
        else
        {
            sKS_EFT_Setup = SKS_EFT_Setup::find();
            sKS_APR_SRS1Attachment          = sKS_EFT_Setup.APSRS1Attachment;
            sKS_APR_SRS1FileName            = sKS_EFT_Setup.APSRS1FileName;
            sKS_APR_SRS1FileType            = sKS_EFT_Setup.APSRS1FileType;
            sKS_APR_SRS1EmailSSRS           = sKS_EFT_Setup.APSRS1EmailSSRS;
            sKS_APR_SRS2Attachment          = sKS_EFT_Setup.APSRS2Attachment;
            sKS_APR_SRS2FileName            = sKS_EFT_Setup.APSRS2FileName;
            sKS_APR_SRS2FileType            = sKS_EFT_Setup.APSRS2FileType;
            sKS_APR_SRS2EmailSSRS           = sKS_EFT_Setup.APSRS2EmailSSRS;
        }

        if (msks_eftap_File.EFTFileType == SKS_EFT_Module::AP)
        {
            container   conReportOptions; 
            if (isAttachmentMissed && sKS_APR_SRS1Attachment && sKS_APR_SRS1EmailSSRS != '')
            {
                conReportOptions += mSKS_EB_Vendor.APHideDueDateAtt1;
                preliminaryRemitAdvice.addAPAttachmentToMail(msks_eftap_File,
                                                SKS_EFT_FileDetail_Remit,
                                                sKS_APR_SRS1FileName,
                                                sKS_APR_SRS1FileType,
                                                sKS_APR_SRS1EmailSSRS,                                                        
                                                messageBuilder,
                                                NoYes::yes,
                                                SKS_EFT_FileDetail_Remit.EFTAP_IHBBankAccount,//SKS IHB 9615
                                                conReportOptions
                                                );
                isAttachmentMissed =  false;

            }
            if (isAttachmentMissed && sKS_APR_SRS2Attachment && sKS_APR_SRS2EmailSSRS != '')
            {
                conReportOptions += mSKS_EB_Vendor.APHideDueDateAtt2;
                preliminaryRemitAdvice.addAPAttachmentToMail(msks_eftap_File,
                                                SKS_EFT_FileDetail_Remit,
                                                sKS_APR_SRS2FileName,
                                                sKS_APR_SRS2FileType,
                                                sKS_APR_SRS2EmailSSRS,                                                        
                                                messageBuilder,
                                                NoYes::yes,
                                                SKS_EFT_FileDetail_Remit.EFTAP_IHBBankAccount,//SKS IHB 9615
                                                conReportOptions
                                                );
                isAttachmentMissed =  false;
            }
        }

        if(isAttachmentMissed && msks_eftap_File.EFTFileType == SKS_EFT_Module::AP)
        {
            warning("@SKS:SKGWarningAttachmentMissed");
        }
    }
    // CR-9496 end
}

void clicked()
{
    super();
    str locFilePathName;
    locFilePathName = msks_eftap_File.FilePath + '\\' + msks_eftap_File.FileName;
    SKS_EB_FileHub::fileDisplayForm(locFilePathName,msks_eftap_File.BFBankIntegration);
}
