Public boolean isPPNumeric(str strValue)
{
    Boolean   xppBool;
    System.Boolean netBool;
    Str matchPattern = '^[0-9]*$';
    System.Text.RegularExpressions.Match myMatch;
    new InteropPermission(InteropKind::ClrInterop).assert();
    myMatch = System.Text.RegularExpressions.Regex::Match(strValue,matchPattern);
    netBool = myMatch.get_Success();
    xppBool = netBool;
    CodeAccessPermission::revertAssert();
    Return xppBool;
}

void disableCompanyInfo()
{
    boolean enabledProperty;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    ;

    if (SKS_EFT_Bank.CompanyFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }

    SKS_EFT_Bank_ACHCompanyID.enabled(enabledProperty);
    SKS_EFT_Bank_ACHCompanyName.enabled(enabledProperty);
    Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == vendpaymmodetable.SKS_EFT_FormatId && SKS_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_fileformatpaymspec.Selected == NoYes::Yes;
    If (sks_eft_fileformatpaymspec)
    {
        SKS_EFT_Bank_ACHOrigDFI.visible(True);
        SKS_EFT_Bank_ACHOrigDFIName.visible(True);

    }
    else
    {
        SKS_EFT_Bank_ACHOrigDFI.visible(False);
        SKS_EFT_Bank_ACHOrigDFIName.visible(False);
    }
    RefreshEFTFromSetup.enabled(enabledProperty);
    SKS_EFT_Bank_ACHOrigDFI.enabled(enabledProperty);
    SKS_EFT_Bank_ACHOrigDFIName.enabled(enabledProperty);

}

