void disableFileHeader()
{

    boolean enabledProperty;
    ;

    if (SKS_EFT_Bank.FileFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }

    SKS_EFT_Bank_ACHImmDest.enabled(enabledProperty);
    SKS_EFT_Bank_ACHImmDestName.enabled(enabledProperty);
    SKS_EFT_Bank_ACHImmOrig.enabled(enabledProperty);
    SKS_EFT_Bank_ACHImmOrigName.enabled(enabledProperty);
    SKS_EFT_Bank_ACHFileIDModifier.enabled(enabledProperty);
    SKS_EFT_Bank_FillBlock.enabled(enabledProperty);
    RefreshFromSetup.enabled(enabledProperty);

    // Hide options if off in file format
    if(sks_eft_fileFormat.BlockFill)
    {
        SKS_EFT_Bank_FillBlock.visible(true);
        SKS_EFT_Bank_FillBlock.enabled(enabledProperty);
    }
    else
    {
        SKS_EFT_Bank_FillBlock.visible(false);
    }

    if(sks_eft_fileFormat.CrLf)
    {
        SKS_EFT_Bank_FileCrLf.visible(true);
        SKS_EFT_Bank_FileCrLf.enabled(enabledProperty);
    }
    else
    {
        SKS_EFT_Bank_FileCrLf.visible(false);
    }
}

void disableFileInfo()
{
    boolean enabledProperty;
    ;

    if (SKS_EFT_Bank.APFileFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }

    SKS_EFT_Bank_APFileName.enabled(enabledProperty);
    SKS_EFT_Bank_APFilePath.enabled(enabledProperty);
    //SKS_EFT_Bank_ControlFileName.enabled(enabledProperty);
    SKS_EFT_Bank_APBankFabricFileLocation.enabled(enabledProperty);
    //SKS_EFT_Bank_APBankFabricFileNames.enabled(enabledProperty);
    SKS_EFT_Bank_APArchive.enabled(enabledProperty);
    SKS_EFT_Bank_APPathArchive.enabled(enabledProperty);
    SKS_EFT_Bank_APACHInclDR.enabled(enabledProperty);
    SKS_EFT_Bank_APDelete.enabled(enabledProperty);
    SKS_EFT_Bank_APHeadTrailID.enabled(enabledProperty);
    SKS_EFT_Setup_CommID.enabled(enabledProperty);
    CommSetup.enabled(enabledProperty);
    SKS_EFT_BankHolidayGroupId.enabled(enabledProperty);
    SKS_EFT_BankCharacterReplacementGroupId.enabled(enabledProperty);

    RefreshAPEFTFromSetup.enabled(enabledProperty);

    // Always hide prenotes if off in setup or file format
    if(sks_eft_setup.APReqPreNote && sks_eft_fileFormat.PreNote)
    {
        SKS_EFT_Bank_APACHPreNote.visible(true);
        SKS_EFT_Bank_APACHPreNote.enabled(enabledProperty);
    }
    else
    {
        SKS_EFT_Bank_APACHPreNote.visible(false);
    }

    // Hide option if off in file format
    if(sks_eft_fileFormat.IncludeCpnyDebit)
    {
        SKS_EFT_Bank_APACHInclDR.visible(true);
        SKS_EFT_Bank_APACHInclDR.enabled(enabledProperty);
    }
    else
    {
        SKS_EFT_Bank_APACHInclDR.visible(false);
    }

    this.BankFabricFieldsInit();
}

void BankFabricFieldsInit()
{
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        SKS_EFT_Bank_APFileName.visible(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APFilePath)).visible(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APControlFileName)).visible(false);
        SKS_EFT_Bank_APPathArchive.visible(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, InboundFileLoc)).visible(false);
        FXAllInbound_FXAInboundConfirmationFilePath.visible(false);
        FXAllInbound_FXAInboundSettlementFilePath.visible(false);
        FXAllOutbound_FXAOutboundPmtInstrFilePath.visible(false);
        FXAllOutbound_FXAOutboundTradeFilePath.visible(false);
        FXAllOutbound_FXAOutboundTradeFileName.visible(false);
        FXAllOutbound_FXAOutboundPmtInstrFileName.visible(false);
        SKS_EFT_Bank_APArchive.visible(false);
    }
    else
    {
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricFileLocation)).visible(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricFileNames)).visible(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricInboundFileLocation)).visible(false);
        FXAllInbound_FXABankFabricInboundTradeAckFileLoc.visible(false);
        FXAllInbound_FXABankFabricInboundSettlementConfFileLoc.visible(false);
        FXAllOutbound_FXABankFabricOutboundPmtInstrFileLoc.visible(false);
        FXAllOutbound_FXABankFabricOutboundTradeFileLoc.visible(false);
    }
}

void disablePostedPaymInfo()
{
    if(SKS_EFT_Bank.PostedPaymSetupDefaults == NoYes::Yes)
    {
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, allowpostedpmtreversal)).allowedit(false);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, RevJournalName)).allowedit(false);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, RevPaymMode)).allowedit(false);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, AutoPostPaymentJournal)).allowedit(false);
    }
    else
    {
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, allowpostedpmtreversal)).allowedit(true);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, RevJournalName)).allowedit(true);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, RevPaymMode)).allowedit(true);
        SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, AutoPostPaymentJournal)).allowedit(true);
    }
}

public void enabledisablecontrolfile()
{
    if(SKS_EFT_Bank.APControlFileFormatiD == '')
    {
        SKS_EFT_Bank.APControlFileName = '';
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APControlFileName)).allowEdit(false);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricFileNames)).allowEdit(false);
    }
    else
    {
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APControlFileName)).allowEdit(true);
        SKS_EFT_Bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricFileNames)).allowEdit(true);
    }
}

