public void enableDisableBasedOnIntegrationType()
{
    boolean enabledProperty;

    //Have "Integration type" field display "File" or "API" depending on method of payment linked to a format.
    if (mAPIFormatType == SKS_EFT_APIFormatType::API)
    {
        SKS_EFT_IntegrationType.text('API');
        enabledProperty = false;

        //Rename the "File info" tab to "API info".
        APEFT.caption("@SKS6760");

        //Change tooltip of Inbound communications id to "Identifier of inbound communications option"
        SKS_EFT_Bank_InboundCommID.helpText("@SKS6761");

        //Change "Wire/IAT ordering party" tab label to "Ordering party".
        Wire.caption("@SKS1255");

        SKS_EFT_Bank_InboundCommID_API.visible(true);
    }
    else if (mAPIFormatType == SKS_EFT_APIFormatType::File)
    {
        SKS_EFT_IntegrationType.text('File');
        enabledProperty = true;

        //Rename the "API info" tab to "File info".
        APEFT.caption("@SKS547");

        //Revert tooltip of Inbound communications id to blank.
        SKS_EFT_Bank_InboundCommID.helpText('');

        //Change "Ordering party" tab label to "Wire/IAT party".
        Wire.caption("@SKS1474");

        SKS_EFT_Bank_InboundCommID_API.visible(false);
    }

    //Set specific elements to be invisible if "Integration type" is "API" or visible if "File":   

    //"Company info" and "File header" tabs.
    CompanyInfoTab.visible(enabledProperty);
    FileHeader.visible(enabledProperty);

    //Below fields (under "API info").
    SKS_EFT_Bank_APFileName.visible(enabledProperty);
    SKS_EFT_Bank_APFilePath.visible(enabledProperty);
    SKS_EFT_Bank_APBankFabricFileLocation.visible(enabledProperty);
    SKS_EFT_Bank_APArchive.visible(enabledProperty);
    SKS_EFT_Bank_APPathArchive.visible(enabledProperty);
    SKS_EFT_Bank_APHeadTrailID.visible(enabledProperty);
    SKS_EFT_Bank_APDelete.visible(enabledProperty);
    SKS_EFT_Bank_InboundFileLoc.visible(enabledProperty);
    SKS_EFT_Bank_APBankFabricInboundFileLocation.visible(enabledProperty);
    SKS_EFT_Bank_ControlFileFormatiD.visible(enabledProperty);
    SKS_EFT_Bank_ControlFileName.visible(enabledProperty);
    SKS_EFT_Bank_APBankFabricFileNames.visible(enabledProperty);
    SKS_EFT_Bank_MALFileName.visible(enabledProperty);
    SKS_EFT_Bank_MALCommID.visible(enabledProperty);


    //"Company info" and "File header" tabs.
    CompanyInfoTab.visible(enabledProperty);
    FileHeader.visible(enabledProperty);

    //"Inbound file information" group header.
    InboundFileInfo.visible(enabledProperty);

    //Make sure the following fields stay visible.
    SKS_EFT_Bank_APFileFromSetup.visible(true);
    SKS_EFT_BankHolidayGroupId.visible(true);
    SKS_EFT_BankCharacterReplacementGroupId.visible(true);
    SKS_EFT_Setup_CommID.visible(true);

}

public void enableDisableInboundInfo()
{
    VendPaymModeTable   lvendpaymmodetable;
    sks_eft_fileformat  lSKS_EFT_FileFormat;

    select firstonly lvendpaymmodetable
            where lvendpaymmodetable.PaymMode == SKS_EFT_Bank.PaymMode
        join
            lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.FormatId == lvendpaymmodetable.SKS_EFT_FormatId;

    if(lSKS_EFT_FileFormat.InboundFileGroupId == '')
    {
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, inboundfileloc)).allowedit(false);
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, inboundCommID)).allowedit(false);
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricInboundFileLocation)).allowedit(false);
    }
    else
    {
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, inboundfileloc)).allowedit(true);
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, inboundCommID)).allowedit(true);
        sks_eft_bank_ds.object(fieldNum(SKS_EFT_Bank, APBankFabricInboundFileLocation)).allowedit(true);
    }
}

void EnableMAL()
{
    if(element.MastAcctListEnabled(SKS_EFT_Bank.PaymMode))
    {
        MAL.enabled(true);
    }
    else
    {
        MAL.enabled(false);
    }
}

public SKS_EFT_Bank findInboundThatUsesOutboundPath(SKS_EB_FilePath _FXAPath, SKS_EFT_Bank _SKS_EFT_Bank)
{
    SKS_EFT_Bank locSKS_EFT_Bank;

    //Check and verify neither of the current outbounds use this path
    if(_SKS_EFT_Bank.FXAInboundTradeAckFilePath == _FXAPath
        || _SKS_EFT_Bank.FXAInboundSettlementConfFilePath == _FXAPath)
    {
        locSKS_EFT_Bank = _SKS_EFT_Bank;
    }

    //if this bank is OK, check all companies
    if(!locSKS_EFT_Bank)
    {
        select firstOnly crosscompany  locSKS_EFT_Bank
            where (locSKS_EFT_Bank.FXAInboundTradeAckFilePath == _FXAPath
                        || locSKS_EFT_Bank.FXAInboundSettlementConfFilePath == _FXAPath)
                    && locSKS_EFT_Bank.FXAenabled == NoYes::Yes
                    && locSKS_EFT_Bank.dataAreaId != curext();
    }
    return locSKS_EFT_Bank;
}

public SKS_EFT_Bank findOutboundThatUsesInboundPath(SKS_EB_FilePath _FXAPath, SKS_EFT_Bank _SKS_EFT_Bank)
{
    SKS_EFT_Bank locSKS_EFT_Bank;

    if(_SKS_EFT_Bank.FXAEnabled == NoYes::Yes
        && (_SKS_EFT_Bank.FXAOutboundPmtInstrFilePath == _FXAPath
            || _SKS_EFT_Bank.FXAOutboundTradeFilePath == _FXAPath))
    {
        locSKS_EFT_Bank = _SKS_EFT_Bank;
    }

    //if this bank is OK, check all companies
    if(!locSKS_EFT_Bank)
    {
        select firstOnly crosscompany  locSKS_EFT_Bank
            where (locSKS_EFT_Bank.FXAOutboundPmtInstrFilePath == _FXAPath
                        || locSKS_EFT_Bank.FXAOutboundTradeFilePath == _FXAPath)
                    && locSKS_EFT_Bank.FXAenabled == NoYes::Yes
                    && locSKS_EFT_Bank.dataAreaId != curext();
    }
    return locSKS_EFT_Bank;
}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys;
    ;

    super();
    tmpSKS_PP_Sys = new SKS_PP_Sys();

    select mastacctlist from sks_eft_fileformat;

    if ( !tmpSKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {
        if (!element.args().caller())
        {
            throw error("@SYS96590");
        }

        select * from sks_eft_setup;
    }

    if(sks_eft_fileformat.MastAcctList == NoYes::Yes)
    {
        MAL.enabled(true);
    }
    else
    {
        MAL.enabled(false);
    }

    if(sks_eft_setup.APAcctApproval == NoYes::No)
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(false);
    }
    else
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(true);
    }

    AttachmentPrompt.text("@SKS:SKGBankAttachName");
    IsVisibleValue.text("@SKS2850");
}

