void loadReversingPaymentfromSetup()
{
    sks_eft_setup   revsks_eft_setup = sks_eft_setup::findByCompany(SKS_EFT_Bank.company());//FDD-10590

    SKS_EFT_Bank.AllowPostedPmtReversal = revsks_eft_setup.AllowPostedPmtJournal;
    SKS_EFT_Bank.RevJournalName = revsks_eft_setup.RevJournalName;
    SKS_EFT_Bank.RevPaymMode = revsks_eft_setup.RevPaymMode;
    SKS_EFT_Bank.AutoPostPaymentJournal = revsks_eft_setup.AutoPostJournal;
}

void load_APIFileFormatType()
{
    SKS_EFT_FileFormat sks_eft_FormatType;

    sks_eft_FormatType = SKS_EFT_FileFormat::find(vendpaymmodetable.SKS_EFT_FormatId);
    mAPIFormatType = sks_eft_FormatType.APIFileFormatType;

}

void loadSKS_EFT_FileFormat()
{
    ;

    if(SKS_EFT_Bank.PaymMode != vendpaymmodetable.PaymMode)
    {
        select SKS_EFT_FormatID from vendpaymmodetable
            where vendpaymmodetable.PaymMode == SKS_EFT_Bank.PaymMode
        join
            sks_eft_fileFormat where sks_eft_fileFormat.FormatId == vendpaymmodetable.SKS_EFT_FormatId;
    }
}

public boolean MastAcctListEnabled(PaymMode _paymMode)
{
    vendpaymmodetable  lvendpaymmodetable;
    SKS_EFT_FileFormat lSKS_EFT_FileFormat;
    ;

    select firstonly SKS_EFT_FormatId from lvendpaymmodetable where lvendpaymmodetable.paymmode == _paymMode;
    select firstOnly MastAcctList from lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.FormatId == lvendpaymmodetable.SKS_EFT_FormatId;

    return lSKS_EFT_FileFormat.MastAcctList;
}

void setMaskAndLengthValues()
{
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspecCpny;
    Int   CpnyNM;
    ;

    if(vendpaymmodetable && sks_eft_fileformat)
    {
        //Company Info
        CpnyNm = 0;
        Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == vendpaymmodetable.SKS_EFT_FormatId && sks_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_Fileformatpaymspec.Selected == NoYes::Yes;
        Select firstonly FormatID from sks_eft_fileformatpaymspecCpny
            where sks_eft_fileformatpaymspecCpny.formatid == vendpaymmodetable.SKS_EFT_FormatId && sks_eft_fileformatpaymspecCpny.CpnyIDNameReq == NoYes::Yes &&
            sks_eft_fileformatpaymspecCpny.Selected == NoYes::Yes;

        //Display CompanyID
        If(sks_eft_fileformat.CompanyIdTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFT_Bank_ACHCompanyID.label(sks_eft_fileformat.CompanyIdTitle);
            element.setProperties(sks_eft_fileformat.CompanyIdMask, SKS_EFT_Bank_ACHCompanyID);
            SKS_EFT_Bank_ACHCompanyID.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFT_Bank_ACHCompanyID.visible(False);
        }
        //Display Company Name
        IF(sks_eft_fileformat.CompanyNameTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFT_Bank_ACHCompanyName.label(sks_eft_fileformat.CompanyNameTitle);
            element.setProperties(sks_eft_fileformat.CompanyNameMask, SKS_EFT_Bank_ACHCompanyName);
            SKS_EFT_Bank_ACHCompanyName.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFT_Bank_ACHCompanyName.visible(False);
        }

        If (!CpnyNm)
        {
            SKS_EFT_Bank_CompanyIdUnique.visible(False);
            sks_eft_bank_companyidppd.visible(false);
            SKS_EFT_Bank_CompanyNamePPD.visible(false);
            SKS_eft_bank_companyidCCD.visible(false);
            SKS_EFT_Bank_CompanyNameCCD.visible(false);
            SKS_EFT_Bank_CompanyIDIAT.visible(false);
            SKS_EFT_Bank_CompanyNameIAT.visible(false);
            SKS_EFT_Bank_CompanyNameCTX.visible(false);
            SKS_EFT_Bank_CompanyIDCTX.visible(false);

        }
        else
        {
            SKS_EFT_Bank_CompanyIdUnique.visible(True);
            SKS_EFT_Bank_CompanyIdUnique.visible(true);
            sks_eft_bank_companyidppd.visible(true);
            SKS_EFT_Bank_CompanyNamePPD.visible(true);
            SKS_eft_bank_companyidCCD.visible(true);
            SKS_EFT_Bank_CompanyNameCCD.visible(true);
            SKS_EFT_Bank_CompanyIDIAT.visible(true);
            SKS_EFT_Bank_CompanyNameIAT.visible(true);
            SKS_EFT_Bank_CompanyNameCTX.visible(true);
            SKS_EFT_Bank_CompanyIDCTX.visible(true);

            If(SKS_EFT_Bank.CompanyIDUnique==True)
            {
                sks_eft_bank_CompanyIDPPD.enabled(True);
                sks_eft_bank_CompanyIDCCD.enabled(True);
                sks_eft_bank_CompanyIDIAT.enabled(True);
                sks_eft_bank_CompanyIDCTX.enabled(True);
                sks_eft_bank_CompanyNamePPD.enabled(True);
                sks_eft_bank_CompanyNameCCD.enabled(True);
                sks_eft_bank_CompanyNameIAT.enabled(True);
                sks_eft_bank_CompanyNameCTX.enabled(True);
            }
            else
            {
                sks_eft_bank_CompanyIDPPD.enabled(False);
                sks_eft_bank_CompanyIDCCD.enabled(False);
                sks_eft_bank_CompanyIDIAT.enabled(False);
                sks_eft_bank_CompanyIDCTX.enabled(False);
                sks_eft_bank_CompanyNamePPD.enabled(False);
                sks_eft_bank_CompanyNameCCD.enabled(False);
                sks_eft_bank_CompanyNameIAT.enabled(False);
                sks_eft_bank_CompanyNameCTX.enabled(False);
            }
        }

        if(sks_eft_fileformat.ImmDestNameTitle)
        {
            //Immediate destination
            SKS_EFT_Bank_ACHImmDest.label(sks_eft_fileformat.ImmDestTitle);
            element.setProperties(sks_eft_fileformat.ImmDestMask, SKS_EFT_Bank_ACHImmDest);

            SKS_EFT_Bank_ACHImmDestName.label(sks_eft_fileformat.ImmDestNameTitle);
            element.setProperties(sks_eft_fileformat.ImmDestNameMask, SKS_EFT_Bank_ACHImmDestName);
            SKS_EFT_Bank_ACHImmDestName.visible(True);
        }
        else
        {
            SKS_EFT_Bank_ACHImmDestName.visible(False);
        }

        IF(sks_eft_fileformat.ImmOriginNameTitle)
        {
            //Immediate originator
            SKS_EFT_Bank_ACHImmOrig.label(sks_eft_fileformat.ImmOriginTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginMask, SKS_EFT_Bank_ACHImmOrig);

            SKS_EFT_Bank_ACHImmOrigName.label(sks_eft_fileformat.ImmOriginNameTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginNameMask, SKS_EFT_Bank_ACHImmOrigName);
            SKS_EFT_Bank_ACHImmOrigName.visible(True);
        }
        else
        {
            SKS_EFT_Bank_ACHImmOrigName.visible(False);
        }

        /*
        //Wire Plus values
        SKS_EFT_Bank_W01.label(sks_eft_fileformat.WBank01Description);
        element.setProperties(sks_eft_fileformat.WBank01Mask, SKS_EFT_Bank_W01);

        SKS_EFT_Bank_W02.label(sks_eft_fileformat.WBank02Description);
        element.setProperties(sks_eft_fileformat.WBank02Mask, SKS_EFT_Bank_W02);

        SKS_EFT_Bank_W03.label(sks_eft_fileformat.WBank03Description);
        element.setProperties(sks_eft_fileformat.WBank03Mask, SKS_EFT_Bank_W03);

        SKS_EFT_Bank_W04.label(sks_eft_fileformat.WBank04Description);
        element.setProperties(sks_eft_fileformat.WBank04Mask, SKS_EFT_Bank_W04);*/

        Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == vendpaymmodetable.SKS_EFT_FormatId && sks_eft_fileformatpaymspec.PaymSpec == 'IATB'
            && sks_eft_Fileformatpaymspec.Selected == NoYes::Yes;

        if(sks_eft_fileformatpaymspec)
        {
            GatewayOperators.visible(True);
        }
        else
        {
            GatewayOperators.visible(False);
        }
    }
}

void setProperties(str _maskValue, FormStringControl _formControl)
{
    ;

    if(_maskValue)
    {
        _formControl.visible(true);

        if (substr(_maskValue, 0, 1) == 'U')
        {
            _formControl.changeCase(2);
        }

        _formControl.limitText(strLen(_maskValue));
    }
    else
    {
        _formControl.visible(false);
        _formControl.changeCase(0);
        _formControl.limitText(0, AutoMode::Auto);
    }
}

void SKS_setEFTTab()
{
    if(sks_eft_setup.APAcctApproval)
    {
        IsVisibleValue.visible(false);
    }
    else
    {
        IsVisibleValue.visible(true);
    }
}

/// <summary>
/// Gets the internal contact information from TAS parameter setup.
/// </summary>
// SKS_AP_Remit attachment
void sks_APLoadInternalContactInfoFromSetup()
{
    SKS_EFT_Bank.APContactName  = sks_eft_setup.APContactName;
    SKS_EFT_Bank.APContactPhone = sks_eft_setup.APContactPhone;
    SKS_EFT_Bank.APContactEmail = sks_eft_setup.APContactEmail;
}

/// <summary>
/// Method used to disable internal contact. SKS_AP_remit attachment
/// </summary>
// SKS_AP_remit attachment
void sks_APdisableInternalContact()
{
    boolean enabledProperty;
    ;

    if (SKS_EFT_Bank.AP_EMFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }
    SKS_EFT_Bank_APContactName.enabled(enabledProperty);
    SKS_EFT_Bank_APContactPhone.enabled(enabledProperty);
    SKS_EFT_Bank_APContactEmail.enabled(enabledProperty);
}

void BankFabricTableLookup(Args _args, SKS_BF_FileLocCategory _category,SKS_BF_LookupTable _lookuptable)
{
    SKS_BF_TmpLookupTable TmpFileLocations;
    Query query = new Query();

    QueryBuildDataSource qbds;

    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup;

    TmpFileLocations = SKS_BF_TmpLookupTable::construct(_category,_lookuptable);

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),_args.parmObject());

    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));

    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));

    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));

    sysTableLookup.parmTmpBuffer(TmpFileLocations);

    sysTableLookup.performFormLookup();
}

public void init()
{
    super();
}

public void create(boolean _append = false)
{
    super(_append);

    SKS_EFT_Bank.CompanyFromSetup = 1;
    SKS_EFT_Bank.FileFromSetup = 1;
    SKS_EFT_Bank.APFileFromSetup = 1;
    SKS_EFT_Bank.APEMFromSetup = 1;
    SKS_EFT_Bank.PostedPaymSetupDefaults = 1;

    SKS_EFT_Bank.AP_EMFromSetup = NoYes::Yes;
    element.sks_APLoadInternalContactInfoFromSetup();
    // 8663 CR -->
    element.loadReversingPaymentfromsetup();
    element.loadCompanyInfoFromSetup();
    element.loadFileHeaderFromSetup();
    element.loadFileInfoFromSetup();
    element.loadEMailFromSetup();
    // 8663 CR <--
}

public boolean validateWrite()
{
    boolean ret;
    VendPaymModeTable  lVendPaymModeTable, locFXAVendPaymModeTable;
    boolean locFXAllRetVal;
    SKS_EFT_FileFormat locSKS_EFT_FileFormat;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFT_Bank_APBankFabricFileLocation.valueStr() && mAPIFormatType == SKS_EFT_APIFormatType::File)
        {
            warning(strFmt("@SKS2643","@SKS:SKGBFAPFileLocation")); //required field

            SKS_EFT_Bank_APBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFT_Bank_APFileName.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS1237")); //required field

            return false;
        }

        if(!SKS_EFT_Bank_APFilePath.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS1246")); //required field

            return false;
        }

        if(SKS_EFT_Bank.APArchive && !SKS_EFT_Bank.APPathArchive && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning(strFmt("@SKS2643","@SKS75")); //required field

            return false;
        }
    }

    if(SKS_EFT_Bank.APCommID == '')
    {
        warning(strFmt("@SKS2643","@SKS2444")); //required field

        return false;
    }

    if(element.MastAcctListEnabled(SKS_EFT_Bank.PaymMode))
    {
        if(SKS_EFT_Bank.MALFileName == '')
        {
            warning(strFmt("@SKS2643","@SKS2629")); //required field

            return false;
        }

        if(SKS_EFT_Bank.MALOutCommID == '')
        {
            warning(strFmt("@SKS2643","@SKS2625")); //required field

            return false;
        }
    }

    if(SKS_EFT_Bank.InboundCommID != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Bank.APBankFabricInboundFileLocation == '' && mAPIFormatType == SKS_EFT_APIFormatType::File)
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFInbFileLocation"));

                return false;
            }
        }
        else
        {
            if(SKS_EFT_Bank.InboundFileLoc == '')
            {
                warning(strFmt("@SKS2643","@SKS2716"));

                return false;
            }
        }
    }

    if(SKS_EFT_Bank.AllowPostedPmtReversal == NoYes::Yes)
    {
        if(SKS_EFT_Bank.RevPaymMode == '')
        {
            warning(strFmt("@SKS2643","@SKS3012")); //required field

            return false;
        }

        if(SKS_EFT_Bank.AutoPostPaymentJournal == NoYes::Yes)
        {
            select firstonly Paymstatus from lVendPaymModeTable where lVendPaymModeTable.PaymMode == SKS_EFT_Bank.RevPaymMode;

            if(lVendPaymModeTable.PaymStatus != CustVendPaymStatus::None)
            {
                error("@SKS3014");

                return false;
            }
        }
    }

    if(SKS_EFT_Bank.APControlFileFormatiD != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Bank.APBankFabricFileNames == '' && mAPIFormatType == SKS_EFT_APIFormatType::File)
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFFileNames")); //required field

                return false;
            }
        }
        else
        {
            if(SKS_EFT_Bank.APControlFileName == '')
            {
                warning(strFmt("@SKS2643","@SKS3242")); //required field

                return false;
            }
        }
    }
    //FXA VALIDATIONS
    if(SKS_EFT_Bank.FXAEnabled == NoYes::Yes)
    {
        //initialize the return value
        locFXAllRetVal = true;

        //OUTBOUND
        // Verify the outbound format supports FXA
        select firstonly locSKS_EFT_FileFormat
            join SKS_EFT_FormatId, RecId From locFXAVendPaymModeTable
                where locSKS_EFT_FileFormat.FormatId == locFXAVendPaymModeTable.SKS_EFT_FormatId
                    && locFXAVendPaymModeTable.SKS_EFT_PaymMode == 1
                    && locFXAVendPaymModeTable.PaymMode == SKS_EFT_Bank.PaymMode;
            //if the format assigned to this method of payment is not supporting multiple Effective dates
            //  then it cannot be used for FXAll.
        if(locSKS_EFT_FileFormat.SupportsMultipleEffectiveDates == NoYes::No)
        {
            locFXAllRetVal = false;
            error(strFmt("@SKS3695", locFXAVendPaymModeTable.SKS_EFT_FormatId));
        }

        // INBOUND
        //Verify inbound FXA Communiction ID is set
        if(strLen(SKS_EFT_Bank.FXAInboundCommID) == 0)
        {
            error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAInboundCommID ) ).labelText())); //required field
            locFXAllRetVal = false;
        }


        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            // OUTBOUND
            //Verify outbound BankFabric payment instruction file location
            if(strLen(SKS_EFT_Bank.FXABankFabricOutboundPmtInstrFileLoc) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXABankFabricOutboundPmtInstrFileLoc ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            //Verify outbound BankFabric trade file location
            if(strLen(SKS_EFT_Bank.FXABankFabricOutboundTradeFileLoc) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXABankFabricOutboundTradeFileLoc ) ).labelText())); //required field
                locFXAllRetVal = false;
            }

            // INBOUND
            //Verify inbound BankFabric trade acknowledgement file location
            if(strLen(SKS_EFT_Bank.FXABankFabricInboundTradeAckFileLoc) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXABankFabricInboundTradeAckFileLoc ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            //Verify inbound BankFabric settlement confirmation file location
            if(strLen(SKS_EFT_Bank.FXABankFabricInboundSettlementConfFileLoc) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXABankFabricInboundSettlementConfFileLoc ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
        }
        else
        {
            // OUTBOUND
            //Verify outbound FXA Trade file name
            if(strLen(SKS_EFT_Bank.FXAOutboundTradeFileName) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundTradeFileName ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            else if(strStartsWith(SKS_EFT_Bank.FXAOutboundTradeFileName, ' ') || strEndsWith(SKS_EFT_Bank.FXAOutboundTradeFileName, ' '))
            {
                error(strFmt("@SKS3696",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundTradeFileName ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            //Verify outbound FXA Trade file Path
            if(strLen(SKS_EFT_Bank.FXAOutboundTradeFilePath) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundTradeFilePath ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            //Verify outbound FXA Payment Instructions file name
            if(strLen(SKS_EFT_Bank.FXAOutboundPmtInstrFileName) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundPmtInstrFileName ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            else if(strStartsWith(SKS_EFT_Bank.FXAOutboundPmtInstrFileName, ' ') || strEndsWith(SKS_EFT_Bank.FXAOutboundPmtInstrFileName, ' '))
            {
                error(strFmt("@SKS3696",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundPmtInstrFileName ) ).labelText())); //required field
                locFXAllRetVal = false;
            }

            //Verify outbound FXA Payment Instructions Path
            if(strLen(SKS_EFT_Bank.FXAOutboundPmtInstrFilePath) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAOutboundPmtInstrFilePath ) ).labelText())); //required field
                locFXAllRetVal = false;
            }

            // INBOUND
            //Verify inbound FXA Confirmation path path isn't blank
            if(strLen(SKS_EFT_Bank.FXAInboundTradeAckFilePath) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAInboundTradeAckFilePath ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
            //Verify inbound FXA Settlement path isn't blank
            if(strLen(SKS_EFT_Bank.FXAInboundSettlementConfFilePath) == 0)
            {
                error(strFmt("@SKS2643",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAInboundSettlementConfFilePath ) ).labelText())); //required field
                locFXAllRetVal = false;
            }
        }

        //Response for all inbound and outbound;
        if(!locFXAllRetVal)
        {
            return locFXAllRetVal;
        }
    }
    ret = super();
    //<SKS-begin>
    ret = SKS_EFT_Bank.SKS_DD_FileLocationValide(SKS_EFT_Bank);
    //</SKS-end>

    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Bank.APEMTemplateNoAttach, SKS_EFT_Bank.APEMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Bank.APEMTemplateAttach, SKS_EFT_Bank.APEMTemplateLanguage) && ret;

    //SKS_FDD_16447/16448--->
    if(ret)
    {
        SKS_EFT_FileFormatFields         lSKS_EFT_FileFormatFields;
        SKS_EFT_BankAccountPaymLookup    lSKS_EFT_BankAccountPaymLookup;

        while select required from lSKS_EFT_FileFormatFields join lSKS_EFT_BankAccountPaymLookup
            where lSKS_EFT_BankAccountPaymLookup.FileFormatFieldsRecId == lSKS_EFT_FileFormatFields.recid &&
            lSKS_EFT_BankAccountPaymLookup.BankPaymRecId == SKS_EFT_Bank.RecId
            && lSKS_EFT_FileFormatFields.Required == NoYes::Yes
        {
            if(lSKS_EFT_BankAccountPaymLookup.fixedvalue == '' && lSKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId == 0)
            {
                error("@SKS2682");
                return false;
            }
        }
    }
    //SKS_FDD_16447/16448<---

    return ret;
}

public int active()
{
    int ret;

    ret = super();


    element.loadSKS_EFT_FileFormat();
    element.load_APIFileFormatType ();

    element.disableCompanyInfo();
    element.disableFileHeader();
    element.disableEMail();
    element.disableFileInfo();
    element.disablePostedPaymInfo();
    element.sks_APdisableInternalContact();

    element.setMaskAndLengthValues();
    element.EnableMAL();
    SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, APPathARchive)).enabled(SKS_EFT_Bank.APArchive);
    element.enableDisableInboundInfo();
    element.enableDisableControlFile();
    element.enableDisableFXAllControls(SKS_EFT_Bank.FXAEnabled == NoYes::Yes);

    element.enableDisableBasedOnIntegrationType();

    if(SKS_EFT_Bank.PostedPaymSetupDefaults == NoYes::Yes)
    {
        element.loadReversingPaymentfromsetup();
    }

    if(SKS_EFT_Bank.CompanyFromSetup == NoYes::Yes)
    {
        element.loadCompanyInfoFromSetup();
    }

    if(SKS_EFT_Bank.APFileFromSetup == NoYes::Yes)
    {
        element.loadFileInfoFromSetup();
    }

    if(SKS_EFT_Bank.APEMFromSetup == NoYes::Yes)
    {
        element.loadEMailFromSetup();
    }

    return ret;
}

public void modified()
{
    super();
    SKS_EFT_Bank_ds.object(fieldnum(SKS_EFT_Bank, APPathARchive)).enabled(SKS_EFT_Bank.APArchive);
}

public void modified()
{
    super();

    SKS_EFT_Bank.APCommUserProg = ''; //clear this field
}

public void modified()
{
    super();

    element.enabledisablecontrolfile();
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret && SKS_EFT_Bank.APEMAttachmentName != '')
    {
        ret=SKS_EFT_SendMail::validateAttachment(SKS_EFT_Bank.APEMAttachmentName, SKS_EFT_Module::AP);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        ret = SKS_EB_FileHub::validateFileName(SKS_EFT_Bank.APFileName);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        ret = SKS_EB_FileHub::directoryExistsForm(SKS_EFT_Bank.APFilePath);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        ret = SKS_EB_FileHub::directoryExistsForm(SKS_EFT_Bank.APPathArchive);
    }
    return ret;
}

public void modified()
{
    super();
    element.enableDisableFXAllControls(this.getValue() == NoYes::Yes);
}

public boolean validate()
{
    SKS_EB_FileHub locSKS_EB_FileHub = SKS_EB_FileHub::construct();
    boolean ret;
    SKS_EFT_Bank locSKS_EFT_Bank;

    ret = super();

    if(ret && strLen(SKS_EFT_Bank.FXAInboundSettlementConfFilePath) > 0)
    {
        if(strStartsWith(SKS_EFT_Bank.FXAInboundSettlementConfFilePath, ' ') || strEndsWith(SKS_EFT_Bank.FXAInboundSettlementConfFilePath, ' '))
        {
            error(strFmt("@SKS3696",SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAInboundSettlementConfFilePath ) ).labelText())); //required field
            ret = false;
        }
        if(ret && !locSKS_EB_FileHub.runDirectoryExists(SKS_EFT_Bank.FXAInboundSettlementConfFilePath)) //Validate the path.  Always runs server-side
        {
            error(strFmt("@SYS55831", SKS_EFT_Bank.FXAInboundSettlementConfFilePath));
            ret = false;
        }
        else
        {
            locSKS_EFT_Bank = element.findOutboundThatUsesInboundPath(SKS_EFT_Bank.FXAInboundSettlementConfFilePath, SKS_EFT_Bank);
            if(locSKS_EFT_Bank)
            {
                error(strFmt("@SKS3697",
                                SKS_EFT_Bank_ds.object( fieldNum( SKS_EFT_Bank, FXAInboundSettlementConfFilePath ) ).labelText(),
                                locSKS_EFT_Bank.company(),
                                locSKS_EFT_Bank.BankAccountId));
                ret = false;
                locSKS_EFT_Bank = null;
            }
        }
    }

    return ret;
}

