{
    super();

    element.disablePostedPaymInfo();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, Name));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(LedgerJournalName, JournalType));
    queryBuildRange.value(enum2str(LedgerJournalType::Payment));
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

public int active()
{
    int ret;

    ret = super();

    if(SKS_EFT_FileFormatFields.FieldType == SKS_EFT_CodeFieldType::CodeLookup)
    {
        SKS_EFT_FileFormatFields_FixedValue.enabled(false);
        Referencegrouplookup.enabled(true);
    }
    else
    {
        SKS_EFT_FileFormatFields_FixedValue.enabled(true);
        SKS_EFT_FileFormatFields_FixedValue.limitText(strLen(strRTrim(SKS_EFT_FileFormatFields.fixedmask)), AutoMode::Fixed);//SKS#DevOps:15839
        Referencegrouplookup.enabled(false);
    }

    return ret;
}

/// <summary>
/// Validate before saving
/// </summary>
/// <returns>true or false</returns>
public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(SKS_EFT_FileFormatFields.Required == NoYes::Yes && SKS_EFT_BankAccountPaymLookup.fixedvalue == '' && SKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId == 0)
        {
            error("@SKS2682");
            return false;
        }
    }

    return ret;
}

